INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (5763, 'Java开发工程师', '10001-15000元/月 ', '新华网股份有限公司', 'http://jobs.zhaopin.com/415728824250528.htm', '国企', '3-5年', '北京', '本科', '岗位职责:1.参与系统的需求调研和需求分析，撰写相关技术文档；2.负责本公司的软件产品设计、开发和管理维护工作；3.对公司其他部门业务提供技术支撑；4.负责与第三方服务厂商沟通、协调；5.对外合作、经营协作、成果推广等技术相关工作；6.岗位相关的其他工作。岗位要求：1.三年及以上互联网应用项目开发经验，大学本科以上学历；2. JAVA基础扎实，精通io、多线程、集合，网络等基础框架；3.熟悉Linux系统基本操作，可编写shell脚本；4.熟悉tomcat、weblogic、nginx等服务器的配置部署等，有OpenResty实战经验优先；5.熟练使用SSH、SSI、SpringMVC等框架进行项目开发；6.熟悉SOA，熟练并使用过rpc框架，有dubbo实战经验的优先；7.熟悉MySQL、MongoDB、Oracle,Redis、Memcached消息队列等；8.良好的沟通和学习能力，团队合作精神，能独立承担工作。', '0', '2018-03-12 20:31:19', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (5769, 'JAVA开发工程师', '20000-40000元/月 ', '中化石油中心', 'http://jobs.zhaopin.com/371442515253285.htm', '国企', '3-5年', '北京', '大专', '金融组岗位职责：1、根据技术团队负责人的安排，承担系统开发任务；2、根据架构师的设计要求，设计系统功能的技术实现方式；3、根据产品经理的需求设计文档开发具体产品功能。任职要求：任职要求 1、熟练运用Spring、Spring MVC、Spring  Cloud，以及Mybatis框架；2、熟练运用Mysql、Oracle等关系数据库，以及HBase、Redis、MongoDB等非关系数据库；3、熟悉jQuery、javascript、CSS、html、Jsp等前端技术；4、熟悉Tomcat、Nginx、MQ、Apache等中间件的配置与使用；5、熟练应用Linux操作系统环境，熟悉常见命令及shell脚本的使用；6、态度端正，具有良好的沟通能力、理解能力、团队合作能力；7、责任心强，具有良好的编码风格、能承受一定的抗压能力；8、具备金融业务系统（包括但不限于信贷、供应链金融、保理、融资租赁等业务），财务管理系统建设经验的优先考虑。', '0', '2018-03-12 20:31:46', null);
