INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (5921, '中级应用安全测试工程师--201720', '8001-10000元/月 ', '中国人民银行软件开发中心', 'http://jobs.zhaopin.com/000555233250768.htm', '国企', '1-3年', '北京', '不限', '任职要求：1、2年以上安全测试工作经验（技术优秀者可不受限制）；2、熟悉JAVA/PHP等常见的web代码,有渗透和逆向分析经验，熟悉SQL注入、XSS、CSRF、URL跳转、钓鱼、常见的web安全漏洞利用及修复工作；3、熟练掌握多种常用安全测试工具（AppScan、AWVS、Burp Suite、Metasploit、sqlmap等），了解OWASP，能够搭建各类渗透测试的测试环境；4、具备好的文档能力，工作责任心强，可胜任重复性工作，做事执着，关注细节；5、具有较强学习能力和逻辑思维能力；6、良好的沟通表达能力，团队协作能力；7、具有良好的职业道德。工作内容：1、负责WEB应用以及移动终端应用的安全测试；2、编写渗透测试报告和对客户进行信息安全培训；3、跟踪国际/国内安全社区的安全动态，进行安全漏洞分析、研究与挖掘，并进行预警；4、协助做好信息安全风险应急响应工作。5、完成安全评估任务和评估报告的编写工作；6、完成上级安排的其他工作。合则约见，非请勿访，谢谢！', '0', '2018-03-12 20:35:35', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (5995, '标准化工程师--201722', '8001-10000元/月 ', '中国人民银行软件开发中心', 'http://jobs.zhaopin.com/000555233250776.htm', '国企', '1-3年', '北京', '本科', '岗位要求：1、硕士研究生学历，计算机应用或相关专业。熟练掌握JAVA编程、XML编程，熟悉开发流程，具有开发经验，熟悉UML建模，熟练应用Rational Rose软件，具有金融标准化专业背景优先；2、参加工作2年或从事金融标准化工作1年以上，至少精通某一领域专业知识，达到专业水准；3、英语国家四级及以上水平，能够阅读并翻译英文资料；4、具备良好的文字表达能力和沟通协调能力，责任心强，有良好的团队合作精神;5、年龄35周岁以下。岗位职责：1、跟踪金融行业发展动态和发展趋势，提出金融标准化工作建议；2、参与国内金融标准的宣贯和维护工作，并提供标准服务；3、宣传推广金融标准；4、参与标准化项目，在项目组中负责与标准化项目主管单位沟通、协助项目经理制定项目计划和研究方案；5、参与项目研究，撰写标准文档及项目方案；6、根据客户要求组织与项目有关的各种会议和培训。', '0', '2018-03-12 20:35:45', null);
