INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (18956, 'JAVA架构师', '面议', '供销电子', 'https://www.liepin.com/job/1911967040.shtml', '国企-其他', '8年以上', '北京', '本科及以上', '工作内容：1.负责系统框架搭建和系统设计，主导或参与电商相关业务系统的服务化架构与改造。2.主导技术难题攻关，持续提升核心系统在高并发、海量请求下的处理性能。带团队解决高并发、高可靠性等各种技术问题。3.复杂分布式系统的设计、用先进可靠的技术架构支撑公司业务的快速发展。4.深度参与技术路线规划，讨论并提出有建设性的意见。5.主导或参与系统微服务单元模块的架构与完善。注：办公地点：通州／宣武／中关村，三个地点可自行选择任职资格:1.计算机相关专业，专科及以上学历；2.8年及以上java开发经验，至少要有1个以上实际大数据类型的项目架构设计经验。具备大中型项目的系统设计及项目整体规划分析与把控能力。3.熟悉常用数据库软件(Mysql,Oracle,SQLSERVER)的原理和使用，熟悉常用ORM和连接池组件,对数据库的优化有一定的理解。4.熟悉分布式系统的设计和应用，熟悉分布式、缓存、消息、负载均衡等机制和实现，具备集群技术、数据同步、高可用、可容灾分布式系统设计能力，熟练使用Spring、SpringMVC、Springboot、SpringCloud、Dubbo、MyBatis等开源框架并了解工作原理。5.熟悉设计模式，熟练掌握多线程开发技术、异步通信处理等。6.良好的沟通能力，团队合作能力，热衷于技术,对新技术以及新的应用比较敏感。7.有一线互联网公司或大型电商平台及微服务相关架构经验者优先。', '0', '2018-03-16 15:49:37', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (18957, 'Java开发工程师', '24-48万', '中化石油中心', 'https://www.liepin.com/job/1911847438.shtml', '国企-其他', '3年以上', '北京', '大专及以上', '职责描述：1、根据技术团队负责人的安排，承担系统开发任务；2、根据架构师的设计要求，设计系统功能的技术实现方式；3、根据产品经理的需求设计文档开发具体产品功能。任职要求：1、熟练运用Spring、SpringMVC、SpringCloud，以及Mybatis框架；2、熟练运用Mysql、Oracle等关系数据库，以及HBase、Redis、MongoDB等非关系数据库；3、熟悉jQuery、javascript、CSS、html、Jsp等前端技术；4、熟悉Tomcat、Nginx、MQ、Apache等中间件的配置与使用；5、熟练应用Linux操作系统环境，熟悉常见命令及shell脚本的使用；6、态度端正，具有良好的沟通能力、理解能力、团队合作能力；7、责任心强，具有良好的编码风格、能承受一定的抗压能力；8、具备金融业务系统（包括但不限于信贷、供应链金融、保理、融资租赁等业务），财务管理系统建设经验的优先考虑。', '1', '2018-03-16 15:49:38', 1);
