INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19026, 'Java开发高级工程师', '18-30万', '中国电信股份有限公司北京研究院', 'https://www.liepin.com/job/1912667562.shtml', '国企-其他', '3年以上', '北京', '统招本科', '职责描述：1.参与行业应用软件开发项目的建设及推广工作，包括客户需求分析、产品原型设计、解决方案和核心代码的编写；2.参与部门关键系统和基础平台的生产运维/运营工作，提出优化建议，并实施落地；3.参与技术攻关和创新技术引用，确保项目的顺利交付，保证项目的进度和质量。任职要求：1.计算机、软件工程等相关专业，本科及以上学历，35岁以下；2.3年以上JAVA开发经验，精通JAVA语言以及J2EE平台特性，熟悉常用的设计模式和开源框架，有大型互联网项目经验或业内知名产品研发经验者优先；3.精通面向对象分析和设计，并能熟练运用建模工具(UML建模工具&PowerDesigner)进行设计工作，且能够指导初中级人员进行开发和设计工作，具有设计和开发对外API接口经验和能力；4.精通SpringMVC、Spring、MyBatis/Hibernate、Freemarker开发，对SOA模式有深入理解，对虚拟机及Linux下的开发环境有较深厚的开发经验；5.熟练掌握分布式通讯框架（Netty、dubbo等NIO框架）、分布式缓存（Memcached集群、redis集群）、分布式事务处理方案；6.精通移动端后台接口设计，对常用的技术框架了解其运行机制和原理，读过源码者优先；7.熟悉XML、HTML、JavaScript（jQuery）、Bootstrap的使用和应用开发以及AJAX技术；8.精通Oracle、MySQL，对数据库有较强的设计能力；9.熟悉Linux环境及shell命令，安装并操作过CentOS或RadHat系统，熟悉SVN、Git的使用，熟悉Maven项目配置管理工具，熟悉Tomcat，Apache，Nginx等开源的服务器的配置和高并发下的性能优化；10.了解常见的安全漏洞，预防和修复方法。', '0', '2018-03-16 15:49:55', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19032, 'Java开发工程师', '18-30万', '中国移动通信有限公司研究院', 'https://www.liepin.com/job/1912576273.shtml', '国企-其他', '3年以上', '北京', '统招本科', '职责描述：1.负责应用系统的需求分析及设计；2.与团队及合作伙伴共同完成应用系统的开发及交付；3.负责应用系统日常维护、定制化开发及缺陷修复。任职要求：学历及专业计算机或信息安全相关专业，本科及以上学历，本科至少3年以上工作经验；专业技能1.有良好编程基本功和设计能力,理解面向对象、松耦合设计思想，会应用设计模式解决需求问题；2.精通Java编程语言及相关开发技术，丰富的JavaWeb开发经验，熟悉JavaEE规范下技术及框架；3.熟练掌握HTML5、javascript、css及常见的web前端开发框架、类库；4.熟悉软件开发全过程，熟悉SVN及Git的使用及相关配置管理方法，了解敏捷开发、CI/CD、DevOps等方法论；5.熟悉主流开源数据库（MySQL，PostgreSQL，MongoDB）的部署和应用程序开发经验，了解数据库高可用及调优。6.熟练使用maven、gradle等构件工具，掌握Tomcat、nginx等web服务器；7.熟悉Linux系统及常见服务的安装部署及shell脚本的编写；8.具备良好的编程习惯、文档写作及管理能力，一定的用户需求分析、系统设计的能力。素质具备良好的沟通、协作能力和主观能动性，热爱创新、踏实进取、追求效率；经验从事开JavaWeb开发工作2年以上，具备中大型互联网公司工作经历优先。', '2018年03月02日', '2018-03-16 15:49:57', 1);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19038, 'Java开发/高级Java开发', '14-39万', '中移在线服务有限公司', 'https://www.liepin.com/job/1910273015.shtml', '国企-其他', '3年以上', '北京-西城区', '统招本科', '职责描述：1、负责根据业务需求进行JAVA应用程序开发；2、负责JAVA基础公共框架的开发工作；3、负责跟踪JAVA相关新技术并运用到程序开发中。任职要求：教育：全日制大学本科及以上学历；知识：具备软件工程、计算机编程、JAVA语言的相关知识；技能：具备Java/J2EE/Web开发能力；熟悉常用的JavaWeb服务器；熟悉常用的开源框架，如SSH等；熟悉分布式、缓存、异步等机制，具有灵活运用能力；具备SQL开发能力；经验：3年以上开发经验；有大型网站工作经验者优先，熟悉kubernetes，有docker容器化开发及微服务架构经验者优先。', '2018年02月26日', '2018-03-16 15:49:58', 1);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19040, 'Java开发架构师、高级工程师', '18-36万', '中国电信集团系统集成有限责任公司', 'https://www.liepin.com/job/1912417986.shtml', '国企-其他', '5年以上', '北京', '本科及以上', '【公司简介】中国电信集团系统集成公司专注电信ToB业务，对内负责中国电信的大型系统的研发与顶层的业务方向的技术支持与搭建，对外为电信的政府和企业客户承接建设大型的信息化项目。公司从电信长途资源管理、智慧光网、大客户网管、拨测、计费等行业应用；天翼对讲、绿色军营等移动互联网应用；云灾备服务平台；天翼信云、天翼云服等云计算信息安全产品；到研发了电信云管理平台、电信大数据飞龙平台、电信开源比翼共享研发平台等一系列大型软件著作专利产品，包括代表中国电信申请了Openstack黄金会员等尖端技术荣誉。公司连年业务增长率30%以上，在全国有32家分公司、1家子公司与4个区域中心定位安全智能的云网平台服务领导者，战略愿景：中国电信DICT时代主力军，助理工业及信息变革。【岗位内容】本次集成总部招聘主要针对公司软件部与云计算研发部重点业务单元与产品服务诚聘英才，岗位如下：A.Java高级工程师岗位职责：1、负责与项目相关人员共同完成模块设计与开发；2、按照项目计划,在保证质量的前提下按时完成开发任务；3、维护和升级现有产品，快速定位并修复软件缺陷；4、与项目相关管理、测试和需求等人员沟通和合作；5、负责相关技术文档的编写。岗位要求：1、精通java预研，对JEE有深入理解，熟悉JEE规范，有3年以上开发经验，有良好的代码编写习惯；2、熟练使用spring框架、mybatis持久层框架；3、熟练使用Oracle、MySql等数据库；4、熟悉HTML、CSS、javascript等Web前端开发技术；5、具有较强的沟通能力，良好规范的编程习惯和技术文档编写习惯，熟悉软件开发设计思想；6、具备较强的逻辑分析及学习能力，有良好的团队合作意识，有强烈的责任心和积极主动的工作态度；7、对新技术持有敏感性，愿意致力于新技术的探索和研究；B.Java架构师职位描述：1、负责分析、设计和搭建公司相关系统的系统架构，解决开发中各种架构及技术问题；2、负责产品核心框架及功能的开发；3、诊断和解决现行系统可能存在的问题，保证公司系统的安全、稳定、高效率运行；4、制定相应的设计规范和开发规范；任职资格：1、全日制本科及以上学历，5年以上研发经验，优秀的需求分析、架构设计和文档编写能力；2、精通Java语言，具备扎实的Java基础、深刻理解JavaEE技术体系架构；3、具有分布式、高并发、高负载、高可用性系统经验；4、具备良好的沟通协作能力、团队合作精神；5、责任心事业心强，工作积极、主动，具有较强的独立解决问题的能力；6、对新业务领域知识具有强烈的学习意愿，并且有较强的学习能力；【公司坐标西直门内冠华大厦，四号线新街口C口】', '2018年02月24日', '2018-03-16 15:49:59', 1);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19041, 'Java程序开发岗', '18-36万', '中国联合网络通信有限公司', 'https://www.liepin.com/job/1911444447.shtml', '国企-其他', '2年以上', '北京', '统招本科', '职责描述：1．按需求完成系统的设计、开发工作；2．负责模块内的业务架构的设计、改善；3．分析现有系统中的性能瓶颈，并实施合理的解决方案，提升现有系统性能；4．积极改善工作，提高产出以及质量；5．对用户需求提供技术支持；6.完成领导交办的其他任务。任职要求：1.计算机、软件工程、信息通信、自动化等相关专业本科以上学历，有2年及以上J2EE开发经验；2.熟悉Spring、MyBatis等框架使用，熟练开发JavaScript、JQuery、CSS等；3.熟练SQL开发，熟悉Oracle等关系型数据库中的一种或几种；4.熟悉Redis、MemCached、ActiveMQ、Kafka等缓存、异步消息中间件的使用，熟悉多线程开发，熟悉JVM调优；5.熟练使用Linux系统、熟练使用Shell、Python等脚本语言的优先；6.乐于接受新事物，工作认真负责，能够承受一定的工作压力。', '2018年02月24日', '2018-03-16 15:49:59', 1);
