INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19069, '高级大数据算法工程师', '36-72万', '中化石油中心', 'https://www.liepin.com/job/1912788783.shtml', '国企-其他', '5年以上', '北京', '本科及以上', '职责描述：1.深度分析数据中的规律，挖掘数据价值。2.参与数据、工具平台相关的功能接口、数据接口，实现业务功能。3.参与数据平台、工具平台的架构、设计以及实现。4.优化数据平台的性能，解决兼容性、功能性Bug，提高稳定性。任职要求：1.全日制本科及以上学历，计算机或相关专业。2.精通Java语言、熟悉Spring、SpringMVC、Mybatis等常用Java框架。3.熟悉RDBMS（比如MySQL）的开发，熟悉SQL语言。4.熟悉Java并发编程，有优良的TroubleShooting能力，能解决各种性能瓶颈（比如IO、CPU、Memory等）。5.熟悉NoSql应用（比如MongoDB、Redis等）的开发经验者优先。6.熟悉常用大数据中间件，有Zookeeper、Kafka、Elasticsearch等开发经验者优先。7.掌握Hadoop、Spark、Storm中至少一种大数据技术，阅读过源代码者优先。8.对大数据技术有钻研热情，乐于分享。', '0', '2018-03-16 15:50:05', null);
