INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19084, '软件开发工程师（产品研发部）', '面议', '中油瑞飞', 'https://www.liepin.com/job/1912572376.shtml', '国企-其他', '3年以上', '北京', '统招本科', '职责描述：1、负责或参与系统架构及功能设计；2、负责系统原型开发及软件功能实现并进行单元测试、模块测试；3、负责系统需求分析和设计；4、按要求完成项目所需文档的编写。任职资格：1、3年以上的java项目工作经验，其中至少有3年以上参与企业级应用的设计和开发经验；2、熟悉敏捷开发的模式、掌握面向对象思想和设计模式；3、精通servlet，JSP，SSH，hiberate等，并熟悉Tomcat、apache、ngix的配置；4、熟练掌握Oracle数据库知识；5、熟悉常用的Java开源框架和Web技术(如HTML,JAVASCRIPT,CSS等)；6、能够独立完成项目经理所分配的软件开发任务；7、具备较强的软件分析和设计能力，能独立设计软件的系统框架；8、良好的沟通能力及团队合作精神，责任心强；9、精通iBatis/MyBatis框架，具备项目中memcached/redis缓存框架的实战经验；10、精通RESTfulWeb服务/基于SOAP的Web服务接口开发框架；11、精通HTML5技术，具备项目中JQuery/ExtJS/Bootstrap富客户端JS框架技术的实战经验。', '0', '2018-03-16 15:50:09', null);
