INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19096, '高级开发工程师', '面议', '建信基金', 'https://www.liepin.com/job/1910939896.shtml', '国企-其他', '3年以上', '北京', '本科及以上', '工作职责:1.协助业务方梳理业务需求，提出技术解决方案；2.负责业务系统的规划设计，制完成重要业务模块及核心框架的搭建及编码实现；3.发现和解决业务系统的技术问题，保证系统的性能和稳定性；4.协同他人组织跨团队沟通协作，确保系统架构内外设计合理或保障项目质量与进度。职责要求:1.知名院校本科以上学历，计算机、软件工程或相关专业；2.3年及以上软件研发经验，具有互联网或者证券行业公司研发和设计经验者优先考虑；3.JAVA基础扎实：熟练掌握数据结构、多线程编程，掌握常用的设计模式；熟悉JVM，包括内存模型、类加载机制以及性能优化；4.精通JAVAWeb平台开发，精通spring、mybatis、springmvc框架，熟悉普元EOS开发平台优先；5.熟悉常见的中间件、分布式解决方案及其原理：分布式缓存、SOA、消息中间件，负载均衡、连接池等；6.具有一定的项目规划和决策能力，善于捕捉业务需求、系统设计中存在的问题，并给出有效的解决措施和方法7.具有很强的分析复杂问题和解决复杂问题的能力，有强烈的责任心和使命感，良好的沟通表达能力和团队协作能力', '2018年03月16日', '2018-03-16 15:50:12', 1);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19099, 'Android开发工程师（应用系统部）', '面议', '中油瑞飞', 'https://www.liepin.com/job/1912869613.shtml', '国企-其他', '2年以上', '北京', '统招本科', '岗位职责：1.负责搭建开发环境，参与需求分析和技术架构设计；2.负责独立完成功能模块开发；3.撰写相关开发文档；4.参与产品单元测试，后期产品升级维护；5.软件的系统联调综合测试完成产品最终发布；6．参于应用的开发设计编写应用代码，负责框架层的修改、优化。知识：1、熟悉Android开发平台及框架原理；2、了解Android系统体系结构，熟悉Android应用程序设计，包括UI、网络等方面；3、熟悉软件开发过程和软件工程方法，具备数据库、网络、硬件的基本理论知识。任职要求：1、2年以上Android平台APP应用开发经验，有开发周期在半年以上的企业级应用，有独立APP应用开发经验者优先；2、熟悉掌握Java语言，熟练使用AndroidUI框架、后台运行、数据存储、网络通信等原理，3、熟悉Android内存管理机制，熟悉第三方控件，熟练使用AndroidStudio及相关调试工具；4、对Android有深刻认识，熟悉Android应用开发框架，有终端软件、设计和架构；5、熟悉Android下网络通信机制，对Socket、TCP/IP以及无线通信协议有一定理解和经验；6、有扎实的开发功底，熟悉多线程、本地数据库、第三方类库；与团队成员充分、有效沟通协作，进行技术风险评估、项目时间评估；7、具有良好的沟通能力、团队合作精神、认真负责；8、具有较强的学习能力，分析、判断和概括能力和逻辑思维能力，可独立承担开发任务。工作地点：河北涿州/北京八角可选', '0', '2018-03-16 15:50:13', null);
