INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19102, '大数据开发工程师', '12-24万', '中国电信集团系统集成有限责任公司', 'https://www.liepin.com/job/1912868845.shtml', '国企-其他', '1年以上', '北京-西城区', '本科及以上', '岗位职责：1、负责大数据离线分析处理；2、面向业务目标，从数据模型、数据分布、数据传输、数据存储等方面进行大数据系统的开发；3、负责数据仓库建设、设计、优化和落地；4、负责数据ETL开发、数据平台建设、设计并实现对BI分析、数据产品开发；5、数据仓库需求调研和需求分析。任职要求：1、统招本科及以上学历，计算机相关专业；2、熟悉数据仓库建模理论，了解数据仓库数据分层架构、多维数据模型设计，有2年以上的实际工作经验；3、在用户行为日志采集、海量数据处理、数据建模、业务理解方面有丰富经验；4、熟悉Hadoop/Sparkstreaming/Hive/Hbase/Impala，有一定的Hql/Sql性能调优经验，对Hadoop、Hive、Spark源码有研究更佳；5、熟悉Linux开发环境，熟练掌握至少一种编程语言（Java/Python/SHELL/Scala）；6、优秀的逻辑思维能力和业务需求分析能力，较好的沟通交流能力，善于主动思考和行动；7、有数据仓库架构的开发经验者优先，例如元数据管理、OLAP引擎、数据同步等；8、有数据分析工具平台的开发经验者优先；9、具有良好的学习能力、时间和流程意识、沟通协作能力。', '0', '2018-03-16 15:50:13', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19103, '大数据架构师', '面议', '海关综合信息网', 'https://www.liepin.com/job/1912866635.shtml', '国企-其他', '3年以上', '北京', '本科及以上', '职责描述：1、负责公司大数据平台的设计和开发，负责hadoop、storm、spark、yarn等云计算平台的开发和优化；制定数据架构规范，指导团队落地。2、负责数据基础架构和数据处理体系的升级和优化，不断提升系统的稳定性和效率，为公司的业务提供大数据底层平台的支持和保证。3、实施公司数据治理相关工作，实施元数据、主数据、数据生命周期的管理工作。4、研究未来数据模型和计算框架的创新与落地。5、培养和建立公司的大数据团队，对团队进行技术指导。任职要求：1、精通大数据系统架构，熟悉业界数据仓库建模方法及新的建模方法的发展；有DW，BI架构体系的专项建设经验，包括但不限于：数据质量、元数据、主数据管理、数据生命周期等。2、有很强的数据设计抽象能力，善于从复杂的数据问题中找到关键路径；了解数据仓库建设过程中的挑战与问题，有作为技术负责人系统化解决问题的成功案例。3、熟悉文本挖掘、机器学习（Mathout、SparkMLlib)等数据挖掘基本流程，有相关的项目经验；4、熟悉目前正在发展的大数据分布式平台前沿技术的应用；包括但不仅仅限于：hadoop、storm、hive、spark、Kafka、Phoenix等。5、精通Java语言,熟悉Servlet,Hibernate,Spring,Struts,Tomcat,MySQL,OracleDB等技术/工具/框架；6、有Python、R等语言工具开发大数据报表处理经验。', '0', '2018-03-16 15:50:13', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19104, '助理开发工程师', '9-12万', '海关综合信息网', 'https://www.liepin.com/job/194806248.shtml', '国企-其他', '经验不限', '北京', '本科及以上', '职位描述：工作描述1、参与金关工程二期等国家重大项目的开发、建设工作；2、承担各类应用集成项目开发和系统升级工作；3、承担项目开发质量管理工作；4、承担各类应用集成项目的技术支持工作；5、参与各类应用集成系统技术创新工作；6、辅助项目经理进行项目管理工作。任职要求：1、熟悉ASP.NET、C#或VB.net等开发语言，熟悉B/S结构程序的开发流程；或熟悉JAVA框架，熟悉JSP开发，熟悉Spring、Struts、Hibernate架构。熟悉Tomcat、Apache、Weblogic等Web服务器产品；2、熟悉SQLServer、Oracle等至少一种数据库系统，掌握SQL语言；3、熟悉Javascript、css、html以及jQuery、MSAjax或其他js框架；4、有良好的软件工程知识和质量意识，能在规范的软件过程管理方法要求下进行编码、测试和编写相关的文档。', '0', '2018-03-16 15:50:14', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19105, '前端开发工程师', '18-30万', '中体彩', 'https://www.liepin.com/job/1912864923.shtml', '国企-其他', '3年以上', '北京', '统招本科', '职责描述：任职要求：1.负责与研发团队密切合作，设计实现网络安全产品的用户界面，确保Web用户界面在多浏览器环境中正常工作；2.负责设计并制作HTML，包括CSS，JavaScript等；3.负责JS前端页面交互设计，提高用户体验；4.理解后端架构，与后端工程师配合，为项目提供最优化的技术解决方案；5.负责产品前端调优，提高产品竞争力。任职要求：1.全日制统招本科及以上学历，计算机相关专业，3年及以上相关工作经验；2.精通CSS，熟悉HTML的DOM结构，可快速制作HTML页面；熟悉HTML5，并在工作中应用过HTML5新特性；3.熟悉原生JavaScript，熟悉jQuery框架，Bootstrap和其他流行的开源JS框架；4.熟悉常见的浏览器的特点和限制，熟悉W3C相关标准和Web常用协议、图片文件格式等；能解决常见浏览器兼容性问题，熟悉IE、Firefox、Chrome等主流浏览器的常见兼容性问题并有可行的解决办法；5.熟悉Java，PHP等后端技术优先。', '0', '2018-03-16 15:50:14', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19106, '高级架构师', '面议', '北京国电通网络技术有限公司', 'https://www.liepin.com/job/1912071402.shtml', '国企-其他', '8年以上', '北京', '统招本科', '职位描述：1、负责行业级客户业务架构、应用架构、数据架构设计，并负责进行整体技术架构设计及落地；2、为行业级客户提供轻量版业务规划、应用规划和数据架构规划，向客户宣讲整体架构方案；3、跟踪互联网行业前瞻性技术应用及电力行业信息化发展动向，不断优化公司信息软件架构及进行技术革新；4、指导研发团队平稳无缝、迭代升级基础架构及底层服务，规范提升架构应用能力；5、与实施团队负责人协同，制定架构优化及底层服务升级的迁移及切换方案，做到技术升级对业务影响最小；6、建立架构解决方案库，整理收集架构解决方案，形成可复用的架构设计资源。任职要求：1、有8年以上行业解决方案或架构设计从业经历，具备移动互联网行业架构设计经验；2、至少两个以上行业细分解决方案方向的行业知识、业务知识、业务架构、应用架构有整体深刻理解，电力行业优先，能够在细分专业领域独立完成客户调研、需求分析、业务设计、架构设计，并可组织研发团队执行落地；3、有3年以上的项目或团队管理经验，有超过两个中型以上项目的项目经理经验（项目人数在10人以上）；4、熟悉Oracle、DB2、MySQL、PostgreSQL、NoSQLDB和Hadoop等数据管理技术至少一种以上；5、对云计算、大数据及在行业的应用和落地有相关经验，熟悉阿里云技术和架构；6、具备优秀的文档能力，使用文字、图示清楚地表达架构意图，能够熟练编写各类技术文档；7、具备Java/Python开发经验，编程基础扎实，熟悉io、多线程等基础框架，熟悉分布式、缓存、消息等机制，具备Tomcat或WebLogic等中间件技术/产品深度使用经验；8、熟悉大数据量、高并发、高性能的分布式系统的设计及应用、调优；9、具有娴熟的沟通技巧，执行力强，具有优秀的团队合作精神、敬业精神；对互联网与云计算行业有清晰的认知和热情。', '0', '2018-03-16 15:50:14', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19107, '系统架构师', '28-49万', '北京国电通网络技术有限公司', 'https://www.liepin.com/job/1912615194.shtml', '国企-其他', '5年以上', '北京', '统招本科', '岗位职责：1、负责大型系统架构的规划和解决方案，大型系统架构设计、概要设计、软件需求规格设计、数据库设计等，指导研发团队进行系统设计开发；2、负责系统技术原则、系统部署方案、系统分层关系、系统分解、系统依赖、异常处理机制、外部集成策略、非功能性需求解决方案等，并对这些进行优化；3、为大型系统做可用性调优，应用环境、数据库环境提供优化方案。任职要求：专业知识及技能要求：1、有10年以上架构设计工作经验，参加过大型项目的设计和架构设计；2、熟练使用JAVA语言，熟悉JavaEE架构开发，熟悉或精通struts、spring、hibernate等框架，熟悉常用JSP、SERVLET、HTML、JAVASCRIPT、XML、JMS、AJAX、WebService等常用相关技术；3、熟悉或精通Oracle等数据库，熟练编写SQL语句，熟练使用PLSQL等工具；精通Weblogic等应用服务器；4、具有面向对象的分析、设计、实现、UI设计的知识和经验，具有较强文档编写能力；5、对各种主流硬件有足够的了解，包括配置、性能、存储，并能根据配置进行比较，选择合适的硬件方案；6、有电力信息化相关开发经验，有大型实际应用系统设计开发、大型项目集成、开发管理经历优先考虑。能力素质要求：1、具有良好的人际沟通、语言表达能力；2、具有很强的执行力和团队合作精神；3、具有较强的学习、领悟能力；4、具有较强的分析问题和解决问题的能力。', '2018年03月16日', '2018-03-16 15:50:14', 1);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19108, 'iOS安全研发工程师', '20-28万', '北京国电通网络技术有限公司', 'https://www.liepin.com/job/1912839714.shtml', '国企-其他', '4年以上', '北京', '统招本科', '岗位职责：1、参与概要设计，编写模块详细设计文档，和相关技术资料；2、负责重要和核心部分代码编写、对开发内容的单元测试、集成测试、安全测试；3、负责解决开发过程中的技术问题，以及安全测评相关问题。任职要求：1、至少3年以上iOS开发经验，1年以上安全研发经验，有扎实的ObjectC功底；2、熟悉ARM汇编，C/C++/Java/ObjC等语言；3、熟悉常用的安全防护方案；4、熟悉iOS体系架构及Objective-C运行时；5、有移动应用、越狱插件开发经验；6、熟练使用lldb、IDAPro等逆向分析工具；能力素质要求：1、视野开阔，具备技术前瞻性，有较强分析和解决疑难问题的能力；2、学习能力强，有创造性思维能力，对移动新技术持有敏感性并愿意致力于新技术的探索和研究；3、富有团队精神,责任感和沟通能力。', '0', '2018-03-16 15:50:15', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19109, '高级开发工程师-物联网平台', '30-60万', '中化石油中心', 'https://www.liepin.com/job/1912855783.shtml', '国企-其他', '3年以上', '北京', '学历不限', '职责描述：1、负责物联网接入平台整体架构设计、体系及其标准制定和功能模块设计；2、负责物联网数据采集层、数据传输层，云服务层、业务应用层的数据传输协议以及接口规范定义及云平台架构搭建；3、组织编写物联网平台设计报告、技术标准和技术文档；4、负责应用需求实现软件架构设计，规划功能模块和接口；5、组织分析并解决物联网相关平台开发过程中的问题。任职要求：1、全日制本科及以上学历，物联网、通信、计算机、软件工程及自动化相关专业；2、5年及以上物联网平台及应用开发经验，精通物联网技术原理，熟悉物联网相关技术趋势,物联网系统设计与研发经验；3、熟悉条码、RFID等自动识别技术，了解多种传感器的工作方式；熟悉无线传感网络、以太网、总线等通讯技术，了解各种通讯网络的传输协议；4、精通C，C++或Java中的一种，有丰富的编程经验；5、精通MySQL，PostgreSQL，redis，oracle中一种数据库，熟悉主流的大数据技术；6、熟悉（至少精通一种）物联网相关技术、通讯协议（WiFi/Zigbee/MQTT/CoAP等）和应用；7、良好的语言沟通能力与协调能力，较强的需求理解和分析能力，具有独立承担项目开发能力，文档编撰能力、项目管理分析能力；8、具备物联网或互联网标准制定、传输协议开发经验者优先。', '0', '2018-03-16 15:50:15', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19110, '高级硬件工程师-物联网平台', '24-48万', '中化石油中心', 'https://www.liepin.com/job/1912854692.shtml', '国企-其他', '3年以上', '北京', '学历不限', '职责描述：1、负责物联网平台相关硬件设备的技术接入方案；2、负责物联网平台相关硬件设备的选型；3、负责硬件接入的实现；4、负责解决设计、测试、生产、运营中出现的各类硬件难题；5、负责物联网平台开发过程相关硬件合作伙伴的维护。任职要求：全日制本科及以上学历，物联网、通信、计算机、软件工程及自动化相关专业；2、3年及以上物联网平台及应用开发经验，精通物联网技术原理，熟悉物联网相关技术趋势,物联网系统设计与研发经验；3、熟悉常见物联网硬件，如RFID、智能终端、智能装备、传感器等。熟悉相关的接口协议及配置，精通实时数据采集及应用技术；4、熟悉无线传感网络、以太网、总线等通讯技术，了解各种通讯网络的传输协议；5、具有传感器硬件接入层的开发经验；6、精通C，C++或Java中的一种，有丰富的编程经验；7、良好的语言沟通能力与协调能力，较强的需求理解和分析能力。', '0', '2018-03-16 15:50:15', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19111, '大数据分析师', '面议', '北京国网信通埃森哲信息技术有限公司', 'https://www.liepin.com/job/1912853938.shtml', '国企-其他', '3年以上', '北京-西城区', '统招本科', '任职要求：1.熟悉统计以及数据挖掘、机器学习、人工智能技术，尤其是关联分析、分类预测、聚类分析、回归分析、时间序列分析等常用分析方法，熟悉Tableau、SPSS等软件，熟悉Python、R等软件包和语言；2.熟悉传统关系数据库、数据仓库技术，熟悉数据预处理技术、数据集成技术、NoSQL数据库技术、内存数据库技术，熟悉Hadoop（包括HDFS、Map-Reduce、HBase、HIVE等）和Spark集群相关技术；3.熟悉JAVA/C/C++程序设计,有并行程序设计经验，可以利用Hadoop的Map-Reduce并行计算框架、通用的MPI并行计算框架设计并行数据分析算法和程序，熟悉分治、动态规划、回溯等算法设计思想；4.熟悉数据可视化技术，设计过数据可视化方案并得到实际的应用；5.有行业大数据分析的成功案例，电力行业大数据分析案例经验者优先。', '0', '2018-03-16 15:50:15', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19119, '自然语言处理工程师', '22-36万', '新华网', 'https://www.liepin.com/job/1912791308.shtml', '国企-上市公司', '3年以上', '北京', '统招本科', '岗位职责：1、负责自然语言处理相关核心技术研究2、负责基于自然语言处理技术的相关系统需求分析、架构设计和系统实现3、负责问答系统的研发，包括语义分析、自然语言理解(NLU)、知识库、自动对话等模块4、负责制定问答系统改进方案5、负责基于知识图谱相关技术构建应用，参与知识图谱业务的产品创新和规划任职要求：1、计算机相关专业，本科及以上学历，3年以上自然语言处理经验2、熟练掌握自然语言处理领域基础理论和常用方法；3、熟悉自然语言处理、问答系统的关键技术，在以下领域之一有深入研究或者相关应用经验：语义分析、语义理解、意图识别、知识图谱、对话管理等；4、熟练使用至少一种开发语言，比如Java,python,C++等5、有知识图谱、自动问答系统、聊天机器人或知识网络研发经验者优先。6、具备优秀的团队意识和沟通能力，学习能力和主动性强', '0', '2018-03-16 15:50:17', null);
