INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19158, '项目经理（社会行业服务）', '18-30万', '沃支付', 'https://www.liepin.com/job/197564612.shtml', '国企-其他', '3年以上', '北京', '本科及以上', '岗位职责:1、负责社会行业客户金融支付项目的全生命周期管理工作。配合联通省分公司集客部、业务拓展部的同事，收集行业客户需求，整理项目需求文档及需求分析文档。2、制定项目解决方案文档，协调公司技术部、运营部、客户资金管理部等相关部门，编制可行的项目方案；3、负责组织、协调项目实施，按照项目序时进度完成；4、负责项目阶段性总结与汇报工作。5、配合相关产品经理进行功能及业务流程验收测试，保证项目品质。任职资格:1、统招本科及以上学历，计算机软件及应用相关专业；2、3年以上工作经验有较强的独立分析、解决问题能力；3、具有2年以上大型软件项目管理经验，熟悉软件工程、软件开发项目生命周期管理，CMMI过程控制，熟练掌握常用项目管理工具；4、有基于Linux/Unix的Java语言大型第三方支付相关系统应用设计开发的经验者优先；5、具有质量管理意识，在项目中主动贯彻执行质量管理规定；6、熟悉WebLogic、WebSphere、JBoss、Apache、Tomcat、Resin等一种或多种主流J2EE应用服务器；熟悉EJB,LDAP,XML,JMS,并熟悉Struts/Spring/JSF等框架；7、良好的沟通能力、团队合作精神和服务意识；善于思考，积极主动、认真负责、具有高度责任感和敬业精神。', '0', '2018-03-16 15:50:26', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19160, '数据分析工程师（大数据开发方向）', '面议', '中油瑞飞', 'https://www.liepin.com/job/1912116332.shtml', '国企-其他', '2年以上', '北京', '统招本科', '岗位职责：1、负责大数据相关产品选型与POC验证；2、参与大数据分析平台的搭建开发，支撑数据挖掘相关功能模块的实现和稳定服务；3、根据各类业务需求，提供数据挖掘解决方案，识别分析需要的各类数据；4、负责数据建模和算法技术研发，包括相关的数据分析、算法建模、模型优化等，挖掘海量数据价值；5、负责相关文档的编写和用户交流。任职资格：1、数学、统计或计算机相关专业本科及以上学历；2、2到5年的数据挖掘相关工作经验；3、了解主流大数据产品，具有相关产品选型和POC经验者优先；4、熟悉常见的数据结构，深入理解分布式算法和系统，具有数据集成、数据建模、数据挖掘相关领域设计和开发经验；5、熟练掌握常用机器学习算法，如logitic回归，决策树，聚类，SVM等算法原理，并有相关建模经验，熟悉模型的评估，及模型结果的解释；6、掌握java、scala、python、R中的一种或多种语言优先；7、熟悉HDFS、Spark、Hive、Storm、HBase等大数据技术，有Spark集群性能优化经验优先；8、需要有责任心,工作积极主动,思路清晰，擅长沟通、有团队合作精神。', '0', '2018-03-16 15:50:27', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19161, '高级算法工程师', '36-72万', '中化石油中心', 'https://www.liepin.com/job/1911979962.shtml', '国企-其他', '3年以上', '北京', '本科及以上', '职责描述：1、基于用户行为数据进行建模、分析和挖掘，生成360度用户画像；2、基于用户画像制定促销策略，并分析促销活动效果，为提升下次活动效果做依据。3、实时监控、分析油站货物（汽柴油、小商品）存量，制定最优的供货策略。4、实时分析竞争对手营销策略，制定自己的定价-营销策略，减少用户流失，提升用户忠诚度。任职要求：1、具有很强的数据分析能力与敏感度，能快速从给定的数据中找出规律。2、熟悉机器学习/数据挖掘相关算法，深入理解逻辑回归、决策树、协同过滤、贝叶斯等算法。3、熟练掌握一门编程语言，如java、python、Scala。4、熟练使用大数据开源工具一种或几种：Hive、Sprark、Storm等。5、对推荐系统、个性化搜索、深度学习有实际应用经验者优先。6、有石油储运、加油站销售经验者优先。', '0', '2018-03-16 15:50:27', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19162, 'APP服务端工程师', '面议', '供销电子', 'https://www.liepin.com/job/1911971239.shtml', '国企-其他', '5年以上', '北京', '统招本科', '"工作内容：1.JAVA5年以上工作经验,参与公司核心产品的设计、研发工作；2.协助制定开发计划并能够分析解决软件开发过程中的问题；3.框架核心代码的编写，代码规范，带领初中级程序员完成项目。4.性能优化，包括代码优化，数据库优化。5.按照部门经理分配的工作，独立、高效的完成编码。6.完成领导交代的其他事项。注：办公地点：通州／宣武／中关村，三个地点可自行选择任职资格:1.计算机相关专业，专科及以上学历，具有5年以上互联网系统开发经验。2.工作态度端正，对技术研发、系统架构相关工作具有浓厚的兴趣；3.精通面向对象分析设计方法，深刻理解J2EE体系架构开发语言，具有中大型互联网项目规划、系统架构设计、开发经验，熟悉常用的设计模式；4.精通J2EE技术体系，熟练使用Spring、SpringMVC、Springboot、SpringCloud、Dubbo、MyBatis等开源框架并了解工作原理。5.熟悉Linux或者Unix操作系统环境下Tomcat、nginx服务器配置。6.熟悉常用关系型数据库的实现(Mysql，Oracle)及redis、MQ等中间件。7.至少熟悉一种代码管理工具（svn、git、vss等）。8.有大型互联网、电商平台工作经验者优先。9.有良好的沟通，团队合作精神、客户意识，有强烈的进取心和钻研精神，善于学习新知识和新技术，有强烈的责任心和主人翁精神，能够承受压力，工作认真、仔细；10.具有电商平台项目经验优先；"', '0', '2018-03-16 15:50:27', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19163, '性能测试工程师', '面议', '供销电子', 'https://www.liepin.com/job/1911969422.shtml', '国企-其他', '8年以上', '北京', '统招本科', '"工作内容：1.根据公司产品现状，优化性能测试流程及性能测试策略，负责产品的性能测试，进行性能基准评估、性能问题定位，参与性能调优并提出改进意见；2.根据性能测试需求，制订性能测试计划及用例、开发性能测试脚本；3.部署、执行性能测试并收集性能监视数据、对性能数据进行分析并提交分析报告，找出性能瓶颈并跟进问题的解决；4.编写各种类型的性能测试方案，如接口，数据库，应用产品的性能测试；5.研究性能测试方法和工具，不断完善性能测试框架，提出平台内性能测试工具和方法。6.建立性能测试基线，持续测试和改进产品性能；7.配合测试团队其他同事完成测试工作，和团队其他成员沟通合作不定期对内进行技术培训注：办公地点：通州／宣武／中关村，三个地点可自行选择任职资格:1.计算机或相关专业，专科以上学历；2.5年以上性能测试相关工作经验，熟悉业界的性能测试流程；3.熟悉软件测试流程与各种测试技巧，熟悉测试计划、测试用例及测试报告的编写4.精通Loadrunner、jmeter及性能测试技术，能够编写相应测试脚本，可配合开发团队进行性能问题定位和分析；5.熟悉Python、java等一种或几种语言；熟悉Oracle、Mysql、SQLserver等常用关系型数据库，能够使用工具观察数据库性能；6.掌握计算机网络、操作系统、数据库、中间件、服务器技术等基本原理；7.掌握多种跨平台性能监控、数据收集和分析的能力8.能够独立完成性能测试可行性分析、测试方案编写、测试工具及脚本调试、监控及结果分析、以及性能调优等工作；9.思路清晰，善于学习、乐于分享，有良好的沟通协调和语言表达能力；10.具备提升团队测试整体水平，梳理并优化测试流程的能力’11.有大型电商平台性能测试经验者优先；"', '0', '2018-03-16 15:50:27', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19164, '中、高级软件测试工程师', '面议', '供销电子', 'https://www.liepin.com/job/1911969357.shtml', '国企-其他', '5年以上', '北京', '本科及以上', '"工作内容：1.负责构建测试环境，产品的功能测试、集成测试和界面测试，进行问题跟踪和管理，保证产品质量；2.建立并完善测试流程，参与需求分析，编写测试计划，测试用例，完成测试报告；3.负责独立完成对项目的单元测试、功能测试、性能测试或自动化软件测试；4.根据软件设计需求制定测试计划，设计测试数据和测试用例；5.参与测试设计，执行软件测试，进行问题记录和分析，给出问题报告；6.编写自动化测试脚本，利用工具完成功能或性能的自动化测试；7.整理实际场景，执行性能测试，分析总结系统瓶颈；8.UI、接口自动化测试；9.根据工作需要，学习调研新技术及测试工具。注：办公地点：通州／宣武／中关村，三个地点可自行选择任职资格:1.计算机相关专业专科及以上学历，测试工作经验3-5年；2.熟悉测试流程，具备编写测试用例、执行测试过程、分析定位问题的能力；3.熟练使用缺陷管理工具，跟踪并推动问题的解决，保障产品质量；4.至少熟练掌握一种自动化测试工具或性能测试工具，如：LR、Selenium、appium等主流测试工具，能根据测试用例编写准确、高质量的测试脚本；5.掌握JAVA、C/C++、Delphi、Basic中至少一种语言，熟悉SQLServer、Oracle、Mysql或者国产数据库中至少一种数据库使用；6.热爱软件测试，可以胜任重复性工作，工作细致认真，有耐心。具备良好的逻辑思维能力，学习能力强，能够迅速掌握新事物。7.有开发经验，且有电商平台测试工作经验优先。"', '0', '2018-03-16 15:50:28', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19166, '系统运维工程师（研究院）', '14-28万', '中油瑞飞', 'https://www.liepin.com/job/1911930858.shtml', '国企-其他', '3年以上', '北京', '统招本科', '岗位职责:1.负责服务器相关应用中间件的安装、部署、配置、监控及维护；2.负责业务应用的安装、配置、升级及日常监控服务；3.负责基础设施(网络、主机、存储)的日常配置、监控及维护。任职资格:1.3年以上系统搭建、维护、诊断、排错经验,有互联网公司或者大型网站运营经验者优先；2.精通linux操作及常用软件的安装和部署，如Ngix、java、tomcat、Apache、redis,rabbitMQ等；3.熟悉常用的一些软件工程类软件的安装、配置和管理，如SVN、Git、禅道、bugzilla、track等；4.至少精通一种shell编程语言，如shell编程、perl、ruby、pathon等；5.有Iaas平台运维经验者优先考虑，包括虚拟主机、虚拟网络、分布式存储等；6.有Paas平台运维经验者优先考虑，如Cloudyfoundry,Kubernetes等；7.精通Mysql数据库的日常运维者优先考虑。', '0', '2018-03-16 15:50:28', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19167, '软件安全工程师（移动平台）', '21-28万', '中油瑞飞', 'https://www.liepin.com/job/1911929413.shtml', '国企-其他', '5年以上', '北京', '统招本科', '岗位职责:1.从事web、APP安全攻防,系统渗透攻防研究；2.负责日常安全检查扫描，及安全分析和安全审计；3.各重要信息系统应急预案的编制和持续修订，信息安全事件事故监督、响应和处理；4.负责运维环境的安全策略实施、安全状态监控、安全日志管理、安全配置管理等工作；5.负责对业务系统实施安全测试、风险评估和安全加固，以及各种入侵、渗透的防范；6.安全设备维护，负责WAF，IDS，IPS，扫描器等安全设备的日常维护和安全报告的输出；7.安全流程建设，负责漏洞挖掘及安全扫描自动化平台建设。任职资格:1.熟悉移动平台开发，有ANDROID或IOS开发经验，了解移动应用技术或移动安全技术；2.熟悉网络设备，了解各类网络协议，有网络组网经验或者了解无线安全，熟悉网络安全技术；3.具备一定Java、Python的编程开发能力，有安全工具开发经验和漏洞验证poc编写能力；4.熟悉主流黑客攻击技术、了解黑客攻击链、熟悉漏洞扫描工具、漏洞利用工具、病毒木马等；5.掌握一门以上的脚本语言，熟悉linuxshell，能自行开发小工具；6.熟悉信息安全的法律、法规和监管要求；7.有较强的学习能力和钻研精神，对网络安全有浓厚兴趣的；8.具有优秀的沟通协调能力和团队精神，能承受一定的工作压力；9.熟悉国家信息安全等级保护体系，有相关项目经验者优先；10.熟练掌握移动端APP(Android或iOS)的安全测试技术、熟悉常见的移动安全漏洞的原理，攻击方式并能结合业务提出修复方案。（加分项）', '0', '2018-03-16 15:50:28', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19168, '架构师', '36-60万', '汇通金财信息科技北京', 'https://www.liepin.com/job/1911914366.shtml', '国企-其他', '5年以上', '北京', '统招本科', '职责描述：1.完成软件系统代码的实现，编写代码注释和开发文档；2.辅助进行系统的功能定义,程序设计；3.根据设计文档或需求说明完成代码编写，调试，测试和维护；4.分析并解决软件开发过程中的问题；5.协助测试工程师制定测试计划，定位发现的问题；6.独立完成接口的开发和联调测试7.配合项目经理完成相关任务目标。任职要求：1.精通Java开发，并有兴趣通过实际写代码解决具体问题。2.熟悉互联网应用架构，熟悉SpringCloud框架。3.有能力独力根据需求进行互联网应用的技术选型和架构设计。4.适应团队合作的文化，能够作为团队一员从系统架构角度对项目做出重要贡献。5.愿意通过实际代码编写在工作中不断磨练自身的技术水平。', '2018年03月16日', '2018-03-16 15:50:29', 1);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19169, '机器学习工程师-集成电路创新中心', '12-36万', '中移物联网', 'https://www.liepin.com/job/1911893106.shtml', '国企-其他', '2年以上', '北京', '统招本科', '工作职责:1、从事物联网行业前沿数据研究，深度学习算法研发。2、应用机器学习算法来处理海量数据，分析与挖掘各种潜在关联，深入挖掘物联网信息的潜在价值3、开发与算法相关的软件，根据实际产品需求设计算法。任职资格:1、本科及以上学历，计算机、数学等相关专业，硕士优先；2、三年以上海量数据挖掘、搜索、推荐相关项目的工作经验；3、熟悉python，C++或者Java中至少一种开发语言；4、了解机器学习的基本算法，如回归、决策树、神经网络、时序分析等；5、有良好的自主学习能力，创新能力和解决实际问题的能力；6、掌握Oracle/MySQL数据库系统、linux操作系统、Python脚本语言者优先；7、熟悉hadoop/spark等一个或多个分布式计算框架/算法平台者优先；8、对机器学习算法在业界的动态、应用、新的算法有热情', '0', '2018-03-16 15:50:29', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19170, '数据建模工程师-集成电路创新中心', '12-36万', '中移物联网', 'https://www.liepin.com/job/1911893105.shtml', '国企-其他', '2年以上', '北京', '统招本科', '工作职责:1、负责项目的需求调研、数据分析、商业分析及数据挖掘建模等工作。2、处理海量数据，经过数据清洗，建立用户画像；3、采用合适挖掘算法，针对数据建模，识别用户潜在商业价值和机会；4、评估数据挖掘结果，不断优化，提升转化率；5、独立研究数据挖掘模型，参与数据挖掘模型的构建、维护、部署和评估工作。任职资格:1、本科及以上学历，计算机、数学等相关专业，硕士优先；2、三年以上数据挖掘、数据分析建模相关工作经验，有推荐系统开发工作经验的优先；3、熟悉python，C++或者Java中至少一种开发语言；4、熟悉常用数据挖掘算法，了解数据挖掘过程和理论方法；5、掌握Oracle/MySQL数据库系统、linux操作系统、Python脚本语言者优先；6、熟悉hadoop/spark等一个或多个分布式计算框架/算法平台者优先；7、有很强的分析问题和解决问题的能力，有强烈的责任心。', '0', '2018-03-16 15:50:29', null);
