INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19194, '大数据应用开发工程师', '面议', '中油瑞飞', 'https://www.liepin.com/job/197521826.shtml', '国企-其他', '3年以上', '北京-昌平区', '统招本科', '岗位职责：1、根据需求，负责相关大数据应用模块设计；2、负责大数据平台及应用研发工作；3、负责代码实现、单元测试、代码修改和维护等工作；任职资格：1、3年以上开发经验2、扎实的计算机编程能力和良好的编程习惯，熟悉linux环境下编程，精通Java/python/scala语言，熟悉常用的shell命令工具，熟悉Git/SVN等源代码版本控制工具，熟练使用Eclipse，idea，Tomcat，Apache等工具；熟悉linux操作系统及命令3、掌握多线程及高性能程序设计编码及性能调优，有高并发应用开发经验优先4、从事过分布式系统的设计、开发、调优工作优先，熟悉Hadoop、Spark、ambari、Hbase、Hive、MongoDB、ElasticSearch、Redis、RabbitMQ等开源项目优先，具有数据挖掘机器学习经验者优先5、良好的沟通交流能力，文字语言表达能力，较好的逻辑分析能力；强烈责任心，开放的性格；擅于协作，具备良好的团队合作精神；善于学习。', '0', '2018-03-16 15:50:35', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19198, '系统运维工程师', '面议', '建信基金', 'https://www.liepin.com/job/1910939969.shtml', '国企-其他', '3年以上', '北京', '本科及以上', '岗位职责:1.负责通用系统平台的运维工作；包含应用系统、应用平台的部署、变更、监控、事件处理、调优，报告编写等。2.精通微软及Linux操作系统使用及相关系统命令；精通各类脚本（如shell，bat，VBA等）的编写；了解python、JAVA等开发工具。3.熟悉Oracle、SQLServer、MySQL等常见数据库的运作原理及简单运维；了解网络相关原理及TCP/IP等常用协议。4.熟悉主流硬件设备的架构，了解硬件设备的运行原理、安装方法及日常维护。任职资格:1.知名院校本科及以上学历，计算机、信息工程或相关专业；2.3年及以上系统运维经验，具有大型金融企业系统运维经验者优先考虑；3.具有一定的项目规划和执行能力，善于处理复杂场景的应用系统问题，并给出有效的解决措施和方法;4.有较强的项目管控能力，团队协作能力，沟通能力，学习能力，责任心及使命感；有较好的抗压能力', '0', '2018-03-16 15:50:36', null);
