INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19212, '高级云计算架构师(职位编号：Richfit000610)', '面议', '中油瑞飞', 'https://www.liepin.com/job/199820850.shtml', '国企-其他', '5年以上', '北京-昌平区', '统招本科', '岗位职责:1.总体把握云计算的技术方向、整体架构、解决方案和未来规划；2.负责云平台整体架构设计和高可靠性和高可扩展性设计；3.负责云平台整体解决方案设计；4.负责云平台以及相关服务(IaaS、PaaS、SaaS)的技术调研及技术决策；5.负责云平台关键模块的技术攻关以及新技术探索；6.负责云计算技术团队培训及建设。任职资格:1.计算机相关专业的本科或硕士学历，5年以上开发/设计/架构经验；2.精通VMwarevSphere，精通OpenStack；3.精通云计算技术特性以及二次开发，精通Python、Java等开发语言；4.熟悉主流虚拟化技术（KVM,XEN,VMware等）；5.熟悉开源的存储系统（Ceph、swift等）；6.熟悉网络虚拟化，对于OpenvSwitch和OpenFlow等有较深入的了解，熟悉SDN；7.熟悉Docker、Kubernetes等容器技术；8.关注云计算技术的发展，具备优秀的学习能力，良好的问题分析与解决能力；9.具备优秀的文档能力，使用文字、图示清楚地表达架构意图，能够熟练编写各类技术文档；10.具有娴熟的沟通技巧，执行力强，具有优秀的团队合作精神、敬业精神。', '0', '2018-03-16 15:50:39', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19215, '工控安全渗透工程师', '面议', '中油瑞飞', 'https://www.liepin.com/job/1910589834.shtml', '国企-其他', '3年以上', '北京-昌平区', '统招本科', '职责描述：1、挖掘工控系统或内网的安全漏洞，并提出加固建议；2、负责工控系统、主机、应用安全检查和渗透测试等工作；3、跟踪国内外信息安全动态；4、进行安全攻防技术研究；5、工控威胁典型场景研究与重现；任职资格：1、熟悉渗透测试步骤、方法、流程、熟练使用一定量渗透测试工具和fuzz测试工具；2、熟悉主流工业控制协议，包括OPC、modbus、profiNET、104、MMS、S7、EtherNetIP等；3、熟悉主流工控设备使用方法，包括PLC编程、上位软件组态、工业通讯等4、熟悉TCP/IP协议、路由、交换技术、主机、数据库相关产品配置及操作；5、熟悉Linux操作系统原理，熟练使用Linux基本命令；6、熟练掌握C/C++、Ruby、Java、Python、Go至少两种编程语言；7、具备漏洞挖掘、逆向分析、模糊测试等经验。', '0', '2018-03-16 15:50:40', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19216, '测试经理', '18-30万', '沃支付', 'https://www.liepin.com/job/196017910.shtml', '国企-其他', '经验不限', '北京', '本科及以上', '岗位职责:岗位职责：1.带领测试团队参与需求讨论，制订测试计划，并与开发/产品团队协作，确保测试质量；2.带领测试团队完成待上线系统的功能测试/性能测试/接口测试/安全测试，提高测试覆盖度；3.带领测试团队引入或者研发自动化测试框架或者工具，不断提升团队的测试效率和测试能力；4.和配置管理、质量管理团队配合，建立自动化的部署、测试和上线推送机制，并在项目执行过程中严格把控质量风险；5.推动高仿真的测试环境和内测发版机制的建立；6.组织测试团队定期进行业务和技术培训；7.和其他部门人员充分沟通，协同工作，从公司的角度出发，提出项目建设过程改进建议。任职要求：1.计算机相关专业，本科或以上学历；2.五年以上软件测试工作经验，三年以上测试团队管理经验；3.精通测试理论和软件工程相关知识，熟悉性能测试、自动化测试和接口测试；4.至少熟悉Python，Java，C++等一门编程语言，熟悉Linux/Unix操作系统和Mysql/Oracle等数据库；5.熟练掌握SVN、Bugzilla、JIRA、QC、Jenkins等管理工具；6.具有快速学习能力和承压能力，良好的沟通能力和组织协调能力；7.有敏捷测试实践经验者、熟悉联通内部业务流程者优先。', '0', '2018-03-16 15:50:40', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19217, '架构师', '面议', '建信基金', 'https://www.liepin.com/job/1910264101.shtml', '国企-其他', '5年以上', '北京', '硕士及以上', '工作职责:1.深入发掘和分析业务需求，对新产品和业务进行系统建模和分析；2.撰写技术方案和系统架构设计，并参与架构评审，形成统一的框架、平台或组件；3.编写系统的核心代码，并参与关键组件和代码的评审，按时交付并对设计质量负责；4.发现和解决开发过程中遇到的性能瓶颈和技术难点。任职资格：1.知名院校硕士以上学历，计算机、软件工程或相关专业；2.5年及以上软件研发经验，具有互联网或者证券行业公司研发和设计经验者优先考虑；3.精通JAVA开发，熟练掌握数据结构、多线程编程，掌握常用的设计模式；熟悉JVM，包括内存模型、类加载机制以及性能优化；4.精通Spring、MyBatis、Quartz、redis、Netty等主流开源框架和技术，对其原理和机制有深入了解；5.精通ORACLE，PL/SQL开发；6.熟悉分布式系统的设计和应用，熟悉分布式、缓存、消息、搜索等机制；能对分布式常用技术进行合理应用，解决问题；7.具有大规模高并发访问的Web应用架构设计和开发经验者优先；8.具有较强的工作积极性、责任心，善于分析和解决问题；9.具备良好的沟通能力和优秀的团队协作能力，具有软件团队管理经验者优先。', '2018年03月16日', '2018-03-16 15:50:41', 1);
