INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19244, '数据分析岗', '面议', '中国电信云公司', 'https://www.liepin.com/job/196469706.shtml', '国企-其他', '2年以上', '北京', '本科及以上', '岗位职责：1、负责数据仓库中1～2个模块的设计、建模、实施、与性能优化；2、负责大数据平台通用组件设计和研发、数据应用业务的代码设计和实现；3、参与基于hadoop平台的二次研发和运维4、组织大数据分析工作的流程、规范和方法建立；任职资格：1、国家统招本科以上学历，计算机相关专业，2年以上开发经验，1年以上项目管理经验；2、精通数据ETL、基于hadoop的数据仓库建模；精通hadoop、hive、hbase、flume、kafka等开源框架及组件；3、熟悉shell或Python或Java语言的使用；4、熟练掌握主流关系数据库技术；精通SQL，有较好的SQL性能调优经验，理解Hive/Mysql/ORCLE基本原理和调优策略；掌握MapReduce的程序开发；5、熟悉MySQL/ORCLE/Redis/HBase等数据库系统，熟悉Hive/impala等开源技术；6、1年及以上大数据分析或者挖掘、BI方面相关工作经验；有电信，金融，互联网等行业大数据平台开发经验者优先；7、掌握数据仓库(DW)/商业智能(BI)/数据统计理论，优先；8、本科及以上学历，计算机科学、软件工程、数学、统计学相关专业毕业。', '0', '2018-03-16 15:50:47', null);
