INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19273, '大数据-平台研发经理', '面议', '中信云网', 'https://www.liepin.com/job/1910936337.shtml', '国企-其他', '5年以上', '北京', '统招本科', '岗位职责：1、大数据平台离线、实时、数据采集、数据提供等工具的组件化开发与实施；2、大数据平台数据逻辑模型与物理模型设计；3、批量与流数据处理设计与开发；4、分析业务需求，开发数据展现模块；5、数据分析，使用机器学习与深度学习等工具进行数据分析与数据模型设计。任职要求：1、本科及以上学历，计算机相关专业，3年及以上大数据技术研发或大数据产品开发工作经验；2、熟悉数据管理基础理论，包括数据质量管理、元数据管理、数据模型等；3、具备ETL开发经验，熟悉Kettle，或者INFORMATIC、DATASTAGE等ETL开发工具；4、熟悉关系型数据库，如MySQL、PostgreSQL、Greenplum数据仓库等；5、熟悉大数据平台HADOOP相关技术，包括HDFS、HBASE、HIVE、SQOOP、SPARK等；6、熟悉大数据平台KAFKA,STORM,SPARKSTREAMING等流式数据处理技术；6、精通SQL脚本编写，能够编写复杂的存储过程；7、精通一门以上脚本语言；(shell/perl/python等)，熟悉java/C/C++等开发语言一种及以上8、熟悉Linux常用命令；9、有机器学习和深度学习经验者优先。', '0', '2018-03-16 15:50:54', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19274, '大数据-平台运维经理', '面议', '中信云网', 'https://www.liepin.com/job/1910936317.shtml', '国企-其他', '5年以上', '北京', '统招本科', '工作职责：1、负责Hadoop/Hive/Hbase/Yarn/Spark/Kafka等组件的运维保障；2、负责Greenplum/Mongodb等NOSQL集群运维保障；2、负责大数据平台自动化运维以及数据化运营平台开发工作；3、负责大数据平台系统的架构审核、业务监控、持续交付、应急响应、容量规划等；4、深入理解大数据平台架构，发现并解决重大故障及性能瓶颈，提供解决方案；5、持续的创新和优化能力，提升产品整体质量，改善用户体验，控制系统成本。任职要求:1、大学本科及以上学历，计算机或者相关专业，3年及以上研发、运维经验；2、深入理解linux系统，运维体系结构，精于容量规划、架构设计、性能优化；专家优先；3、具备一定的开发能力，精通一门以上脚本语言；(shell/perl/python等)，熟悉java/C/C++等开发语言一种及以上；4、具备很强的ownership，故障排查能力，有很好的技术敏感度和风险识别能力；5、能够承受较大的工作压力，以结果和行动为准则，努力追求成功；6、熟悉hadoop、hbase、hive、spark等原理并具备管理，配置，运维经验；7、熟悉分布式系统设计范型，有大规模系统设计和工程实现的了解者优先。', '0', '2018-03-16 15:50:55', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19275, '大数据-高级大数据分析师', '面议', '中信云网', 'https://www.liepin.com/job/1910935180.shtml', '国企-其他', '3年以上', '北京', '统招本科', '岗位职责：1、结合具体业务场景，快速了解业务，发现潜在的特征模型，挖掘现有数据的价值，驱动业务发展和运营。2、从场景和问题出发，灵活运用内外部数据，对业务数据进行场景识别，规划、发现、挖掘企业大数据价值；3、利用统计或者机器学习算法，独立或者合作进行大数据分析，包括但不限于从数据库（HDFS、MongoDB等）中读取数据、数据清洗、数据探索、相关性分析等；数据预处理，包括但不限于特征工程等；数据挖掘模型选择、训练、调参、上线等；对模型进行评估，并持续改进。任职要求：1、两年以上大数据、数据挖掘方面的开发经验；精通大数据分析技术，熟悉整个大数据的完整处理流程，包括数据的采集、清洗、预处理、存储、分析挖掘和数据可视化；2、熟悉数据挖掘相关算法(决策树、SVM、聚类、逻辑回归、贝叶斯)，有扎实的基本功和良好的逻辑思维能力；3、对数据敏感，有强烈的好奇心，喜欢折腾数据并从数据中发现价值；4、熟悉Java或者Python语言，熟悉SQL和ETL相关流程；5、理解数据仓库概念和技术，熟悉MySQL/Hive优先；6、有Hadoop/Hive/Impala/Spark/MPI相关经验者或者其他大规模数据处理经验者优先。', '0', '2018-03-16 15:50:55', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19276, '云计算-高级测试工程师', '24-60万', '中信云网', 'https://www.liepin.com/job/1910607522.shtml', '国企-其他', '5年以上', '北京', '统招本科', '岗位职责：1.完成中信云平台自动化测试脚本开发工作。2.完成自动化测试框架的更新维护工作。岗位要求：1.计算机、软件相关专业，本科以上学历，至少一年自动化测试开发经验。2.熟练使用Python、Java开发语言其中一种。3.熟悉Rest接口规范，了解消息队列中间件至少一种，了解Json数据交换格式。4.了解自动化测试框架的基本组成架构和原理。5.有互联网产品自动化测试、前后端分离、微服务技术经验者最佳。', '0', '2018-03-16 15:50:55', null);
