INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19794, 'MIG03-测试开发工程师（北京）', '18-36万', '腾讯', 'https://www.liepin.com/job/199296629.shtml', '上市公司-其他', '3年以上', '北京-海淀区', '本科及以上', '岗位要求：计算机相关专业本科以上学历，三年以上大型软件测试经验，热爱软件测试，精通软件测试理论和方法；有大型项目的后台测试经验，对需求的理解深入，能够参与项目的架构设计评估，并根据项目特点制定测试方案，设计高效的测试方法、测试用例并执行；精通Linux操作系统、Tcp/Ip网络协议，熟悉MySQL/Oracle等数据库，有C/C++、java、python、shell其中二到三种语言的开发经验；具备自动化测试和性能测试的设计和应用经验，参与过大型后台(linux)系统的自动化和性能测试，并对自动化和性能测试有全面的理解；具备良好的沟通协调和团队协作能力，和优秀的发现问题、分析问题、解决问题的能力，有强烈的责任心和积极主动的工作态度；有大数据项目开发和测试经验者优先。岗位职责：负责海量数据访问系统的后台测试工作：分析系统相关的需求、设计、架构等，制订相应的测试策略和测试计划，从全流程的角度，设计相应的测试方法，达成项目质量目标；跟进建设自动化测试和性能测试，并组织实施，在保障海量用户的系统的正确性、安全性、健壮性下，为团队探索和引进行业相关测试技术和理念，帮助团队在质量和效率上的提升。', '0', '2018-03-16 15:53:20', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (19810, '23048-翻译君机器翻译算法高级工程师(北京)', '面议', '腾讯', 'https://www.liepin.com/job/198763504.shtml', '上市公司-其他', '5年以上', '北京-海淀区', '本科及以上', '岗位职责：负责机器翻译引擎相关算法的研究和开发工作，持续改进翻译系统的翻译质量，主要工作包括：针对用户数据分析结果，提出改进方法，不断优化机器翻译系统的性能；针对模型的不足及难以解决的现象，提出改进建议和方法，提升翻译引擎的性能。岗位要求：机器翻译、计算语言学或自然语言处理专业，硕士及以上学历，5年以上机器翻译从业经历；精通SMT和NMT的基本原理，熟悉不同模型的优缺点，有商用机器翻译引擎的研发经历；熟悉构建SMT系统所需的主要技术；熟悉NMT的主要模型及特点，对RNN、CNN、seq2seq等有很好的把握；熟悉分词、NER等机器翻译常用的NLP技术，能够通过分析数据，快速定位错误，并提出改进建议；精通至少一种编程语言，Python/C/C++/Java等；熟悉tensorflow、theano、torch等深度学习框架。', '0', '2018-03-16 15:53:24', null);
