INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (20312, '知识图谱技术工程师', '36-60万', '汇通金财信息科技北京', 'https://www.liepin.com/job/1913061861.shtml', '国企-其他', '3年以上', '北京', '硕士及以上', '职位：知识图谱技术工程师最低学历：硕士招聘人数：1~2职位描述（1）负责知识图谱底层架构设计与实现、知识挖掘、知识提取、知识表示、知识存储、知识推理、知识图谱可视化、知识图谱应用等相关领域的算法研发工作；（2）完成公司安排的其它数据相关的工作和任务；任职要求：（1）有较强的算法基础，熟悉linux系统,拥有良好的java或python编码能力;熟悉图数据库的各种编程语言接口。（2）具备自然语义处理，知识图谱的相关研究经验，熟悉自然语言处理方向常用技术，命名实体识别，关系抽取，句法分析等。（3）熟悉商用或开源的图数据库，如：Neo4j，OrientDB，熟练使用图语法对图数据库进行模式匹配查询。（4）熟悉RDF规范与SPARQL查询语言。（5）有Hadoop/Spark/ES开发经验优先。（6）有较强的分析问题解决问题能力，工作踏实上进；有良好的团队合作意识。', '0', '2018-03-26 20:07:00', null);
