INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (24163, '资深Java开发工程师/技术专家', '42-84万', '爱奇艺', 'https://www.liepin.com/job/1912970470.shtml', '猎聘-其他', '4年以上', '北京', '统招本科', '职责描述：负责社交互动类app后台、消息平台等核心技术模块的设计与研发持续的进行代码重构和系统优化，保证线上系统的稳定运行研究新技术框架，跟进行业主流的技术和业务发展趋势，并应用到实践中。任职要求：岗位要求本科及以上学历，3年以上Java开发经验。精通多线程编程，jvm、设计模式等相关技术栈，熟练使用Java常用框架和开发工具，如SpringMVC、Struts、Mybatis、Springboot、maven、git等熟悉nginx、tomcat等应用服务器，熟悉linux开发环境，掌握一种脚本语言(shell，python，perl)的使用熟练掌握mysql，熟悉并对hbase、mongodb、elasticSearch、zookeeper等有开发使用经验。熟悉Redis、Memcache、Couchbase等常用缓存及activeMq、kafka等常用消息中间件熟悉高并发、高可用、高性能的分布式系统的设计与开发热爱技术，自我驱动，具有较强的解决问题能力，善于分析总结。良好的沟通能力及团队合作精神，能够承受一定工作压力。特别欢迎拥有以下经验：具有社交、feed流、push、消息类业务的设计开发经验；有高并发，大数据量系统设计开发经验；有（移动）互联网行业工作经历；有vue.js、webpack、node等前端开发经验，有全栈开发经验', '2018年03月28日', '2018-03-28 22:42:57', null);
