INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (24219, 'JAVA开发工程师（开发三室）', '12-30万', '阳光保险集团', 'https://www.liepin.com/job/1912875762.shtml', '猎聘-其他', '2年以上', '北京', '统招本科', '岗位职责：1、依据业务需求文档，完成软件模块的分析、设计、开发、测试，以保证能够按期交付，并稳定运行；2、分析现有系统产生的问题，并给出解决方案，及时修复问题；3、参与相关项目的设计、评审、开发，保障系统能够按照需求达到预期效果；4、跟踪了解业界发展、互联网相关最新技术，参与重点项目新技术研发工作；5、对低阶岗位人员进行技能培训、指导低阶岗位的技术开发工作；6、领导交代的其他任务。任职要求：1、统招本科及以上学历，计算机相关专业，2年以上Java开发经验；2、熟练掌握常见开源框架和技术，如Spring、Mybatis、Jquery、Zookeeper、TestNG、RabbitMQ、Redis等；3、熟悉主流应用系统的架构体系、中间件以及数据库技术，如Jboss、Oracle、Mysql、Mycat等；4、良好的团队合作精神、独立解决问题和沟通能力；5、正直诚实、有责任心，能够承受一定压力；6、对SOA微服务、缓存化技术、数据路由有深入了解者优先；7、有大型项目开发、实施经验者优先；8、有保险行业经验者优先；', '0', '2018-03-28 22:43:22', null);
