INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (24298, 'Java高级研发工程师/专家', '40-80万', '阿里巴巴', 'https://www.liepin.com/job/1912730174.shtml', '猎聘-其他', '经验不限', '北京', '统招本科', '关于我们：我们是蚂蚁金服负责用户拓展以及营销推荐的技术团队，致力于通过数据、技术、算法拓展用户以及提升金融营销效果。我们是一支跨国团队，有美国硅谷来自于Google/Twitter/Facebook/Microsoft等知名公司的技术大拿，也有国内年轻的技术精英，团队多元化，技术氛围浓厚，鼓励技术/业务创新，期待具备扎实技术功底，有技术追求的技术人加入我们。岗位描述：1、开发面向蚂蚁金服的数亿用户的大数据、大规模分布式机器学习和深度学习算法应用平台；2、开发高并发、低延迟的推荐营销业务解决方案，提升消费、支付及金融场景的推荐营销效果；3、助力产品和业务完成技术架构落地，持续提升系统平台的稳定性、研发效率并提升业务效果。岗位要求：1.熟悉Java或C++或Python编程任意一种，熟悉多线程编程，内存管理，设计模式，具有良好的编程习惯。2.熟悉主流应用服务器架构体系、常用中间件及数据库如Tomcat、ActiveMQ、Redis、Memcached、HBase、MySQL等；3.具备良好的系统分析及构建能力，有平台化系统研发经验者优先；4.热爱技术，对新技术有好奇心、善于沟通与团队协作；具备以下经验者优先(加分项)：1.具有较大型互联网公司营销系统/推荐系统/广告系统经验的同学优先；2.有大型分布式、高并发、高负载、高可用性系统设计和稳定性经验优先；3.对Storm、Flink、Hadoop、Spark等大数据平台有所了解，有机器学习算法应用经验者优先', '2018年03月28日', '2018-03-28 22:43:58', null);
