INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (24415, '资深JAVA架构师', '面议', '京东', 'https://www.liepin.com/job/1912516088.shtml', '猎聘-其他', '6年以上', '北京', '本科及以上', '工作职责：1、规划后台系统技术架构，参与需求分析、架构设计、技术决策以及详细设计；2、优化现有系统的性能，解决系统中的关键问题和技术难题；3、把握复杂系统的设计，确保系统的架构质量，核心部分代码编写、指导和培训工程师、不断进行系统优化；4、带领团队攻克例如大数据量、高并发、高可用等带来的各种挑战及技术难关。任职资格:0.平均每段工作经历两年以上，硬性无特批1、工作年限6年以上，3年以上产品研发及架构设计相关经验，独立负责过一个以上的产品研发或两个以上产品核心架构设计工作；2、JAVA基础扎实：熟练掌握数据结构、多线程编程，掌握常用的设计模式；熟悉JVM，包括内存模型、类加载机制以及性能优化；3、精通HTTP编程，7年及以上使用Java语言进行HTTP相关开发经验，精通springmvc、orm框架（ibatis或hibernate），熟悉前端开发（js、html、css）；4、有大型业务系统的架构设计和实施能力，熟悉分布式系统的设计和应用，熟悉分布式、缓存、消息等机制，能对分布式常用技术进行合理应用，解决实际架构问题。5、熟练掌握Zookeeper、Redis、Kafka、Netty、Tomcat、Dubbo、Mysql等流行开源产品者优先；6、有很强的分析复杂问题和解决复杂问题的能力，有强烈的责任心和使命感；7、技术视野开阔，学习和抗压能力好，有很强的分析和解决复杂问题的能力，具备良好的沟通和组织协调能力', '0', '2018-03-28 22:44:51', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (24458, '高级java开发工程师', '42-70万', '京东', 'https://www.liepin.com/job/1911927523.shtml', '猎聘-其他', '5年以上', '北京', '统招本科', '职责描述：1.作为团队核心开发，参与京东中台研发体系业务需求讨论，技术方案确定，以及技术研发和产品上线工作；2.解决各种疑难杂症,系统优化，并且完成横向技术与业务产品平台和组件的沉淀；3.跟进新技术的探索研究，应用到实际产品开发中，不断迭代优化团队，内部技术分享。任职要求：1.至少5年以上Java开发经验，3年以上架构设计经验，精通B／S架构；2.熟悉JavaEE规范，Java技术基础扎实，熟悉IO、多线程、异步处理等基础架构，熟悉缓存、搜索等机制，在可扩展、高性能、高稳定性系统的设计、开发、调优等方面有实际经验；3.对数据库的基本理论和内部实现机制有深刻的理解，能够熟练应用MySQL/NoSQL，有实际大数据量的数据库设计经验；4.架构师要求有良好的组件级建模能力，熟悉NoSQL、MQ、Cache、TCP/IP原理，能够设计复杂业务、高并发、大数据量的系统；5.知识面广，思路开阔，创新能力强，对新技术持有敏感性并愿意致力于新技术的探索和研究；6.有一定的抗压能力。如果以上均不符合，也没关系，只要你确认自己是个技术人才，对技术有足够的热情，也欢迎投递简历。', '2018年03月28日', '2018-03-28 22:45:12', null);
