INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (24532, 'JAVA高级开发工程师-Zrobot合资公司(037962)', '面议', '京东金融集团', 'https://www.liepin.com/job/1912161113.shtml', '猎聘-其他', '3年以上', '北京', '本科及以上', '工作职责：1、主导财富事业部部分核心系统的设计、开发、上线工作；2、与产品经理项目经理一起确认系统需求，制定计划，并对计划实施结果负责；3、线上系统问题快速定位与解决；4、提高初、中级工程师技术水平，保证代码质量和可维护性。ZRobot由中国最具创新力的互联网金融企业京东金融和美国著名的大数据公司ZestFinance合资建立的金融科技公司。我们专注于在中国推动消费者对个人信用的认知以及中小企业信用市场的构建，以帮助个人与企业获取信用产品。根据高维度的变量，利用数据挖掘、机器学习等技术，构建国内业界最全面，最精确，最可信的信用报告，为客户提供完整用户画像，并灵活服务于各种场景。我们为客户提供独立的第三方信用评估服务。基于丰富的场景应用和领先的专业技术，致力于构建大数据背景下，覆盖广泛、功能完备的信用生态体系。任职资格:1、3年以上使用Java语言开发经验，编程基础扎实，精通io、多线程、集合等基础框架；2、精通使用spring、springMVC、mybatis/hibernate、SSH等框架，深入了解其原理和实现机制；3、性格特征：对技术工作有浓厚的兴趣，喜爱开发工作，对新技术有钻研精神；4、具有面向对象设计及编程经验；5、有实际线上项目性能调优经验，能与运维同事良好配合，解决线上问题；6、对跨系统的通信、服务化、异步通信，有实际实施经历；7、有良好的编码规范、并负责撰写项目中研发文档,熟练使用svn等常用版本工具。', '0', '2018-03-28 22:45:46', null);
