INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (24794, '高级Java工程师岗(无人仓系统)', '面议', '京东', 'https://www.liepin.com/job/197440058.shtml', '猎聘-其他', '5年以上', '北京', '统招本科', '工作职责：1、参与项目需求评审、架构设计，软件详细设计；2、负责无人仓机器人系统、shuttle系统等系统设计和核心代码开发；3、参与调度系统的算法开发；4、无人仓系统推广。任职资格:1、本科以上学历，5年以上Java实际项目开发经验；2、有扎实的Java基础，对并发处理、异常处理有丰富的经验，良好的编码风格；3、精通主流开源应用框架，如Spring、ibatis、XML、JSON、Maven等开发技术；4、精通关系型数据库，SQL优化；5、有一定Linux基础；熟悉nginx；6、熟悉SOA架构模式，精通服务治理；7、思维敏捷，具备良好的学习能力、团队协作能力和沟通能力；善于思考，能独立分析和解决问题；8、对业务算法感兴趣，有持续钻研精神，有WMS、WCS系统工作经验者优先。', '0', '2018-03-28 22:47:46', null);
