INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (25132, '银河搜索-Java高级开发工程师-北京-02219', '21-42万', '搜狗', 'https://www.liepin.com/job/198973678.shtml', '猎聘-其他', '2年以上', '北京', '本科及以上', '【项目介绍】银河搜索是垂直搜索部下辖视频、图片、音乐、购物、小说、游戏、金融、汽车等众多垂直领域的产品线。肩负为整个搜狗搜索大部分核心需求提供优质内容及服务的重任。在互联网服务垂直化深度发展的大势面前，尤其是移动互联场景下连接一切的大趋势下，垂直搜索部又有了新的使命：在细分领域做出更有价值的服务型用户产品，开辟新的商业模式，培养产品独立的自增长能力。我们将有很多机会依靠我们大搜索的流量资源、搜索引擎的深厚技术积累结合我们对垂直领域用户需求的理解，在细分领域做深做强。我们专注于垂直细分领域的商业化产品。技术上既有传统搜索的大数据、高并发的挑战，又有类电商的用户交易数据服务系统，和类广告的数据服务系统，技术广度横跨多个典型业务方向。【职位诱惑】大数据、高并发生产环境，负责搜索引擎和类电商系统的核心研发，团队技术底蕴好【特别提示】搜狗欢迎专情的你，最多投递两个职位，请谨慎投递【岗位职责】1、搜狗搜索产品功能和后台服务模块的设计、开发、维护；2、搜狗搜索产品运营系统的开发和维护；3、用户数据的挖掘、分析和统计；4、搜索产品公有平台的开发和维护；任职条件1、三年以上java开发经验，精通java编程语言，深入理解JVM机制，熟悉linux环境开发部署及问题定位和日志分析。2、精通Mysql、Redis、Nginx、SpringMVC、模板引擎、中间件的原理与应用。较熟悉Web开发。3、熟悉消息、异步、多线程、thrift等开源架构及开发技术。4、善于学习新事物，善于沟通和表达，有良好的团队合作意识。', '0', '2018-03-28 22:50:20', null);
