INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (27182, '分布式服务(PaaS/Docker/MQ/dubbo)技术专家', '面议 ', '中国联通', 'http://jobs.zhaopin.com/271309317250302.htm', '其它', '10年以上', '北京', '本科', '岗位职责：1、负责docker的研发方向，负责docker开发方向、以及基于Docker的PaaS课题攻关、技术创新等工作。2、牵头负责Docker、MQ服务优化工作。3、负责分布式中间件服务研发团队的培养以及打造。4、负责Docker、PaaS新技术的技术预研工作。5、负责分布式消息、分布式服务产品的研发工作。任职要求：1、国家统招全日制本科及以上学历，面向计算机、通信工程、电子工程、信息安全等相关专业。2、10年以上中间件相关（开发、维护、预研等）工作经验。3、精通liunx原理，具备MQ、ESB服务研发工作，以及MQ、ESB系统课题攻关、技术创新等工作经验。4、在MQ、ESB开发具有一线实操经验，研发的产品或系统经过大型应用场景的验证；5、熟悉国内外主流的Docker新技术、MQ、Dubbo的产品及技术发展趋势和方向；6、熟悉Kubernetes相关技术框架、算法及相关应用场景，7、具有很强的分析能力、沟通能力、逻辑思维能力和架构编写能力，有较强的团队带动能力。8、具有python、Java的实际工作不少于5年的编程经验', '0', '2018-03-28 22:57:34', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (27184, '资深CDN平台架构师', '面议 ', '中国联通', 'http://jobs.zhaopin.com/271309317250330.htm', '其它', '5-10年', '北京', '本科', '岗位职责：1、CDN行业内资深专家、了解CDN行业的市场环境及商业策略、精通CDN平台架构、开发、建设、节点、调度、产品及优化，构建全球化的CDN服务；2、参与核心代码的编写；3、负责技术方案中关键技术问题的攻关和预研，验证技术可行性，并实现原型；4、负责CDN平台的核心功能的设计和优化，负责关键项目的技术选型和技术方案设计；5、跟进相关领域的技术更新，并将新技术方案输出到研发团队落地到产品中，保证云CDN平台的技术先进性；6、分析线上系统性能优化点（节点、网络、存储、调度）和稳定性（质量度量和容灾等），提高系统利用率，提升平台稳定性；7、跟踪国外相关领域的最新成果，与相关团队一起构建国际领先的CDN平台，并需获得软件著作权、专利等资质证明；8、根据业务发展的需要及客户需求的情况，持续对平台进行完善和优化，并规范所开发软件的版本管理；9、负责建立并牵头开发团队，招聘所需开发人员，落实团队日常管理工作。任职要求：1、本科及以上学历，计算机相关专业；2、8年以上工作经验，3年以上大中型应用系统架构设计经验，精通CDN系统架构，熟悉CDN相关业务和技术，对调度、资源管理、缓存、点直播转码系统有较深的研究，有较强的系统设计和问题解决能力，独立承担过大中型CDN系统或大型视频播放平台架构和设计；3、精通c/c++、python、golang中至少两种编程语言；4、精通Java，熟练应用JSP、Servlet、JDBC、JMS、JPA进行开发，对Spring、Hibernate、Mybatis、SOA等工具类库非常熟悉；5、熟练使用Linux，熟悉常见数据库Mysql、Oracle、SQL Server；6、熟悉互联网领域开源软件，如nginx、squid、Apachetrafficserver、lighttpd、rtmp等大型服务器软件中的一种；7、熟悉HTTP/RTSP/RTMP协议，熟悉TCP/IP协议；8、擅长和软件开发工程师沟通，具备团队合作精神，高度的责任心和优秀的工作态度。9、了解最新的技术及发展趋向，懂得怎样衡量各种设计方法的利弊，懂得平衡各种开发局限的制约；10、具有较强的文档撰写能力和良好的英文阅读能力；11、有Linux kernel、Nginx等开源社区贡献的优先考虑；12、曾经在知名CDN服务提供商类公司具备相关工作经验者优先。', '0', '2018-03-28 22:57:34', null);
