INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (27272, '技术专家/高级技术专家-杭州/北京/深圳(职位编号：GP053980)', '面议 ', '阿里巴巴集团', 'http://jobs.zhaopin.com/000127917291978.htm', '合资', '5-10年', '北京', '本科', '岗位描述：1. 参与集团内部或外部容器、调度相关的产品或项目的设计、研发，并推动落地2. 参与Pouch、Kubernetes等开源项目的建设，扩大阿里在开源社区的影响力3. Cloud Native思想布道，及相关前沿技术的调研与应用4. 推动集团和学术界的交流合作，力争打造产学研一体的技术演进路线岗位要求：1. 精通Kubernetes，对Kubernetes设计理念及各模块源码比较熟悉，参与过源码贡献者优先2. 精通Docker等容器引擎的使用，了解底层实现原理，并对容器网络、存储架构比较了解，熟悉OVS、Calico、Flannel等网络插件3. 熟悉Linux平台，掌握Cgroup/CFS/CAT等资源隔离和调度的原理和用法4. 深入理解Cloud Native思想，并对行业发展趋势有敏锐的嗅觉5. 三年以上JAVA或Golang开发经验，计算机相关专业优先考虑6. 具备较好的逻辑思考能力、沟通能力、学习能力，工作中积极主动、有责任心、抗压性强', '0002-01-01 00:00:00', '2018-03-28 22:57:50', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (27275, '阿里健康-测试开发专家(职位编号：GP054213)', '面议 ', '阿里巴巴集团', 'http://jobs.zhaopin.com/000127917292064.htm', '合资', '3-5年', '北京', '本科', '你将会为人类的健康事业做出贡献，在互联网医疗的变更中，充分体会到阿里健康的紧张刺激和挑战。岗位描述：1、提高软件开发和测试效率。通过自动化或者半自动化方式提高团队测试执行效率和软件开发效率；2、发现软件测试过程中的痛点，负责找到，参与和实现解决方案；3、通过自动化方式，优化现有测试体系中的流程，改善和提升团队测试效率和软件开发效率；岗位要求：1、本科及以上学历，计算机相关专业；2、2年以上web 服务器开发经验，精通apache，tomcat，Nginx任一主流web服务器，了解django，springMVC，struts2，bootstrap，jQuery等前后端技术；3、熟悉Linux操作系统，熟练使用Java/Python, h5等；4、有自动化测试工具开发经验者优先；5、了解android，ios优先；6、了解测试技术者优先。', '0', '2018-03-28 22:57:51', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (27276, '优酷-数据开发资深工程师-北京(职位编号：GP054314)', '面议 ', '阿里巴巴集团', 'http://jobs.zhaopin.com/000127917292100.htm', '合资', '5-10年', '北京', '本科', '岗位描述：1 参与家庭娱乐数据仓库架构设计与研发，建设百PB级的公共数据平台和服务系统，实现高质量数据的互通与共享 2 参与家庭娱乐数据产品与应用的数据研发，发掘数据商业价值，打造极致体验的数据产品 3 助力数据化运营业务，构建丰富多样的BI应用岗位要求：1 从事数据仓库领域至少2年以上，熟悉数据仓库模型设计与ETL开发经验 ，掌握Kimball的维度建模设计方法，具备海量数据加工处理（ETL）相关经验 2 掌握至少一种数据库开发技术：Oracle、Teradata、DB2、Mysql等，灵活运用SQL实现海量数据ETL加工处理 3 熟悉Linux系统常规shell处理命令，灵活运用shell做的文本处理和系统操作 4 有从事分布式数据存储与计算平台应用开发经验，熟悉Hadoop生态相关技术并有相关实践经验着优先，重点考察Hdfs、Mapreduce、Hive、Hbase 5 熟练掌握一门或多门编程语言，并有大型项目建设经验者优先，重点考察Java、Python、Perl 6 熟悉数据仓库领域知识和技能者优先，包括但不局限于：元数据管理、数据开发测试工具与方法、数据质量、主数据管理 7 掌握实时流计算技术，有storm开发经验者优先 8 良好的语言沟通与表达能力和自我驱动动力', '0', '2018-03-28 22:57:51', null);
