INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28028, 'Java开发工程师', '20-39万', '京东', 'https://www.liepin.com/job/1910587497.shtml', '猎聘-其他', '3年以上', '北京', '统招本科', '职责描述：1.主导和参与系统分析设计工作，承担核心架构的代码实现，负责系统改造和升级；2.主导和参与系统性能优化，持续提升系统在高并发、高访问量下的性能和系统稳定性；3.负责指导普通开发工程师，审核工程师的设计和代码质量，帮忙其快速成长；4.带领小团队主导项目的系统分析、设计和核心代码的编写工作，确保研发进度和质量。任职要求：1.3-5年以上Java开发经验，本科以上学历；2.Java基础扎实、理解JVM原理、有多线程、高并发系统开发经验的优先考虑；3.熟悉主流开源框架，如Spring、SpringMVC、MyBatis,了解其原理和实现机制，读过上述框架源码优先考虑；4.熟悉MySQL等关系型数据库，了解基本的sql优化，有sql调优经验者优先；5.熟悉分布式、缓存、消息、异步等机制，有分布式系统、集群架构设计和使用经验优先；6.有Redis，Elasticsearch等实战经验优先；7.善于学习和与人沟通，为人正直、诚信，有较强的事业心和逻辑思维能力；8.有很强的分析问题和解决问题的能力,有强烈的责任心。', '0', '2018-03-28 23:25:30', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28034, '软件开发工程师（高级java）', '面议', '京东', 'https://www.liepin.com/job/199708822.shtml', '猎聘-其他', '5年以上', '北京', '本科及以上', '岗位职责：1.负责与登录，注册，中间件及账户安全产品的开发和维护工作；2.持续对线上系统进行性能优化及稳定性提升；3.负责攻克团队遇到的技术难题；4.拆解产品需求，提出技术实现方案和步骤。职位要求：1.(Java)良好的开发基础，精通Java语言基础，熟练掌握多线程开发技术，熟练掌握常用JAVA开发技术和开源技术,熟悉软件开发流程；2.(JS)熟练掌握Javascript、HTML、CSS、Ajax等前端开发技术；3.(linux),熟悉linux系统,掌握基本的linux使用命令；4.(DB)有NOSQL使用经验，熟悉常用数据库软件(MySQL)的原理和使用，熟悉常用ORM和连接池组件,对数据库的优化有一定的理解；5.(分布式)具备丰富的大型互联网系统设计经验，熟悉分布式、缓存、消息、负载均衡等机制和实现，具备海量数据研发和处理经验优先；6.良好的沟通能力，有责任心，有良好的团队合作合作精神。', '2018年03月28日', '2018-03-28 23:25:33', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28049, 'Java开发专家', '面议', '阿里健康', 'https://www.liepin.com/job/1910104492.shtml', '猎聘-其他', '4年以上', '北京', '本科及以上', '岗位描述：1、从事阿里健康系列产品研发工作；2、根据产品团队的交付物采用敏捷开发的方式进行产品研发；3、从事所属技术域的技术需求开发，通过技术优化不断提升产品的稳定性、可用性、性能；4、编写各类技术文档，注重技术积累，积极思考，为技术域的完善和发展献计献策；5、完成主管交办的其他技术任务；岗位要求：1、本科及以上学历，三年以上JAVA开发经验；2、扎实的JAVA基础知识，熟悉JAVA虚拟机原理，JAVA高级特性和类库，JAVA网络与服务器编程，JAVA多线程编程；3、良好的面向对象分析、设计、开发能力，熟悉各种设计模式；4、熟悉分层的应用框架设计思想，包括：SOA，事件驱动、分布式系统原理、最终一致性等；5、熟练使用主流JAVA开发框架，如Spring、SpringMVC、Struts、Mybatis等；6、熟练掌握SQL语言，熟悉MySQL或ORACLE数据库开发技术及相关工具；7、熟悉Web前端技术，包括HTML/Ajax/Javascript/jQuery等；8、熟练使用Tomcat、Nginx等应用服务器软件；9、熟练使用PowerDesigner、Rose等UML建模工具10、对技术有强烈的兴趣，喜欢钻研，具有良好的学习能力，沟通技能，团队合作能力；11、勇于承担压力与责任，喜欢挑战性的工作，有追求卓越的激情和能力；12、具备大型互联网产品开发经验者优先；13、具备大数据开发经验者优先；', '2018年03月27日', '2018-03-28 23:25:40', null);
