INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28153, '高级java开发工程师', '35-42万', '京东', 'https://www.liepin.com/job/1912934399.shtml', '猎聘-其他', '5年以上', '北京', '统招本科', '职责描述：1、独立完成项目的分析、设计、编码，确保项目的进度和质量；2、技术难题攻关，持续提升核心系统性能，保证系统的安全、稳定、快速运行；3、深入理解业务需求，分析和发现系统的优化点，负责推动产品性能和架构优化；4、承担核心功能代码编写，重点项目架构设计开发；5、新人指导、培训及CodeReview，提升团队整体技术水平；任职要求：1、统招本科及以上学历,计算机或相关专业,5年以上java实际项目开发经验；2、精通Java及面向对象设计开发，对部分Java技术有深入研究，研究过优秀开源软件的源码并有心得者优先；3、精通Spring，MyBatis等流行开源框架；4、精通JVM调优、接口及数据库性能优化等；、5、熟悉Nginx、Redis、HBase、Elasticsearch、ZooKeeper等；6、参与过大型复杂分布式互联网后台系统的设计开发者优先；7、有医疗、房地产项目优先；8、拥有和工作年限相匹配的广度和（或）深度；', '0', '2018-03-29 09:03:21', null);
