INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28197, '研发工程师', '面议', '', 'https://www.liepin.com/rpojob/18061/', '猎聘-股份制企业', '4年以上', '北京', '本科', 'JAVA开发工程师1人T5物业云技术部门岗位职责：1.负责物业云相关系统的开发；2.参与产品需求的讨论和评审，与团队共同制定技术方案并落地执行；3.负责系统架构的设计和研发，对系统的性能、稳定性和扩展性做出正确的评估和技术选型；4.对旧有系统的不合理处提出重构方案，能够以系统化的方式整合和优化各种模块；岗位要求：1.计算机相关本科及以上学历4年以上互联网相关工作经验；2.精通Java编程语言，熟悉ssh开发框架，熟悉基于dubbo/thrift的远程接口开发，熟悉Linux操作系统。3.理解各种常用技术的原理及使用场景，如缓存，注册中心，定时任务，各种类库如guava等4.熟练使用mysql,了解nosql者优先5.了解常用设计模式，并能够运用到实际项目当中6.良好的编程风格，对代码结构、质量、美观有严格的要求7.熟悉敏捷开发项目流程8.具备较强的学习能力，具备较强的沟通能力，追求开发效率，能独立承担并推动项目。', '0', '2018-03-29 09:04:17', null);
