INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28270, 'Android开发工程师', '面议', '今日头条', 'https://www.liepin.com/job/1912603484.shtml', '猎聘-其他', '3年以上', '北京-海淀区', '本科及以上', '工作职责：1、负责今日头条协作平台APP研发，包括但不限于即时通讯(IM)工具开发；2、负责高质量的设计和编码，承担重点、难点的技术攻坚，参与项目架构设计与实现；3、和产品经理配合，深度参与手机产品需求讨论，功能定义等。任职要求：1、良好的设计和编码品味，热爱写代码，能产出高质量的设计和代码；对稳定性和性能有超乎寻常的关注；2、计算机基础扎实，熟悉常见数据结构、算法和设计模式，熟悉DB和网络；3、熟练掌握Java语言,至少一年以上Java使用经验，熟悉Java常用的库；熟悉kotlin开发优先；4、熟悉掌握AndroidApp开发知识和技巧，一年以上开发经验；对AndroidFramework有一定的了解；5、有知名开源作品/有跨端开发能力的优先考虑；7、自我驱动能力、系统性学习能力，良好的沟通协作能力，注重效率。', '0', '2018-03-29 09:43:36', null);
