INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28294, '性能测试工程师（技术保障部）', '面议', '京东', 'https://www.liepin.com/job/1912173984.shtml', '猎聘-其他', '3年以上', '北京', '统招本科', '职责描述：-负责cdn/dns/攻防检测清洗/监控平台/硬件参数/负载均衡等服务的性能评测；-包括但不限于：1）基准测试，特定场景下的架构选型，设备选型；2）性能评测，拐点分析，线上可承载水位度量；3）针对以下方面的调优：配置、部署、数据量、适用场景、技术方案、代码实现；4）灾备方案验证；5）压力测试、稳定性测试6）健壮性评测、异常场景构造任职要求：-本科及以上学历-2年以上性能测试经验，熟练使用发压、监控工具。包括但不限于：jmeter、nmon、jconsole等java类工具。-能主导性能测试方案的设计、数据构造、指标分析-具有可测性方案的提议和推动能力-熟悉基于socket的程序间网络通讯，熟悉常用的网络协议：IP，TCP，HTTP，HTTPS-熟练使用至少一种语言，C,python,perl,go,java等，能根据需要构建测试工具-具有某方面（内存、DB、硬盘读写、网络等）深入的调优能力，有成熟的分析和调优方法。', '0', '2018-03-29 09:43:43', null);
