INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28297, '架构师', '面议', '阳光保险集团', 'https://www.liepin.com/job/199589207.shtml', '猎聘-其他', '8年以上', '北京', '本科及以上', '岗位职责：1.负责需求分析、系统分析，提出技术方案。2.负责主导功能模块设计、数据结构设计、对外接口设计等系统设计工作。3.负责指导研发工程师的产品开发和技术研究工作，解决各类技术疑难问题。4.负责系统架构整体设计，技术架构选型。5.负责技术交流和推广，建立良好的研发氛围，提升团队整体技术水平。6.跟踪并研究最新技术，提出技术研究及可行性报告。7.积极了解业界发展、互联网相关新技术及趋势，参与规划公司未来技术架构方向。任职资格：1.统招本科及以上学历，3年以上Java开发经验，2年以上架构设计经验。2.精通大规模系统的JavaEE架构技术，熟练掌握常见开源框架和技术，如Mybatis、Struts2、Spring、ActiveMQ、Jquery、Ajax、memcached等。3.熟悉主流应用服务器的架构体系、数据库以及中间件技术，如weblogic、jboss、jetty、Oracle、Mysql等。4.能对分布式常用技术进行合理应用并解决系统问题。5.熟悉缓存技术，网站优化，服务器优化，集群技术处理、网站负载均衡、网络存储、网络安全、数据库高可用设计及性能评估机制、系统性能调优等软件编程高级技术。具备以下条件之一或以上者优先考虑：1.拥有保险应用系统架构经验者优先。2.拥有大型门户网站系统架构经验者优先。3.熟悉云计算系统架构者优先。', '0', '2018-03-29 09:43:44', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28298, '高级大数据开发工程师', '22-24万', '京东', 'https://www.liepin.com/job/1912549300.shtml', '猎聘-其他', '5年以上', '北京', '统招本科', '职责描述：1.负责数据分析、加工、清理、处理程序的开发2.从事海量数据分析、挖掘相关工作3.负责数据相关平台的搭建、开发、维护、优化任职要求：1.计算机相关专业，本科及以上学历，5年以上Java开发工作经验，学习能力突出；2.熟悉hadoop生态系统内常见项目的使用（hdfs、hive、hbase、spark、zookeeper,yarn等），具有MapReduce开发经验，有实际大数据项目经验优先3.熟练掌握Oracle、MySql等主流数据库4.精通JAVA，熟悉基于J2EE的WEB架构设计，熟悉Web开发流程，有丰富的WebMVC（Struts、Spring，Hibernate等）开发经验；5.熟悉Linux/Unix系统环境下的操作；熟悉Tomcat等应用服务器的配置和优化；6.具有良好的沟通能力、组织能力及团队协作精神，有较强的分析和解决问题的能力；加分项：1.有海量大数据开发经验2.有Hadoop、Spark、HBase深入源代码分析经验3.熟悉机器学习、数据挖掘、分布式计算4.基础能力+学习能力特别优秀者', '0', '2018-03-29 09:43:45', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28299, '测试开发工程师岗', '面议', '京东', 'https://www.liepin.com/job/1912543725.shtml', '猎聘-其他', '2年以上', '北京', '学历不限', '职责描述：1.负责京东核心事业部的多个大型项目的测试工作；2.参与项目需求、研发设计的相关讨论，从测试的角度帮助提升产品质量，编写、评审测试用例，编写测试报告；3.多角度分析缺陷造成的原因，帮助研发人员快速定位；4.实现部分功能的自动化测试，并搭建持续集成环境。任职要求：1.二年以上互联网行业工作经验；2.熟练使用Java语言，并至少熟悉Python、JavaScript、shell中的一种脚本语言；3.熟练使用Tomcat、Nginx在Linux系统完成应用部署，熟练使用SQL语句；4.熟练使用Jenkins搭建持续集成环境；5.熟练使用Fiddler、Charles抓包工具对HTTP/HTTPS接口进行截取、篡改、伪造等操作；6.了解Dubbo框架的调用原理，可以使用Java或Jmeter对接口进行调用；7.精通软件测试理论、方法和过程，掌握多种自动化测试技术，熟练使用Jmeter、Selenium、Appium等工具；8.熟练使用AndroidStudio、Xcode进行APP进行签名、打包工作；9.有良好的沟通能力，与相关人员密切合作，保证项目的顺利推进；10.对软件测试有浓厚的兴趣和丰富的经验，有很强的分析能力和定位问题的能力.', '0', '2018-03-29 09:43:45', null);
