INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28303, '实时计算平台高级研发工程师【大数据研发部】', '面议', '京东', 'https://www.liepin.com/job/1912183685.shtml', '猎聘-其他', '3年以上', '北京', '统招本科', '职责描述：1、负责京东实时计算平台的架构设计、研发、维护工作；2、保持技术前瞻性，持续推动系统架构的合理性；3、推动团队架构及研发能力的提升。任职要求：1、本科或以上学历，计算机，软件或相关专业；2、5年以上JAVA相关软件开发经验，精通JAVA，深入掌握JVM和并发原理；3、熟悉SparkStreaming、Flink、Storm等至少一种流计算框架；4、熟悉Kafka、MQ等至少一种消息中间件；5、对Hadoop生态相关技术有一定了解，例如HDFS、HBase、Hive等；6、具备较强的排查问题、分析问题、解决问题的能力；7、有中间件开发经验者优先。', '0', '2018-03-29 09:43:46', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28305, '架构师-Zrobot合资公司(037955)', '面议', '京东金融集团', 'https://www.liepin.com/job/1912160588.shtml', '猎聘-其他', '3年以上', '北京', '本科及以上', '工作职责：1、设计和改进高并发WEB平台技术架构；2、参与各项研发规范的制定和基础研发环境的搭建；3、负责架构中间件及基础核心模块的设计研发和演进；4、负责解决项目中存在的架构等技术问题；5、对团队开发人员进行代码质量审核、技术指导和监督；6、跟踪前沿技术，评估并引入适用的新技术。ZRobot由中国最具创新力的互联网金融企业京东金融和美国著名的大数据公司ZestFinance合资建立的金融科技公司。我们专注于在中国推动消费者对个人信用的认知以及中小企业信用市场的构建，以帮助个人与企业获取信用产品。根据高维度的变量，利用数据挖掘、机器学习等技术，构建国内业界最全面，最精确，最可信的信用报告，为客户提供完整用户画像，并灵活服务于各种场景。我们为客户提供独立的第三方信用评估服务。基于丰富的场景应用和领先的专业技术，致力于构建大数据背景下，覆盖广泛、功能完备的信用生态体系。任职资格:1、5年以上JAVA开发经历；2、作为架构师或者主程开发过大型企业级解决方案，对数据结构、算法有深刻理解，有分布式编程经验。对CMS系统，账号权限管理系统，单点登录系统，日志管理，工作流系统，分布式系统的设计思路清晰，能快速上手开发搭建；3、对于兼容性测试，单元测试，集成测试，性能测试，压力测试都有一定的了解，能用开源框架快速搭建测试环境；4、精通数据库系统，会写存储过程，对于数据库性能优化，事务管理，分库分表都有一定的程度的了解；5、能从面向对象原理深入理解设计模式的思想；6、精通常用算法设计思想；7、熟悉敏捷开发模式，对敏捷开发和传统开发模式的优缺点要有深刻的认识；8、了解数据挖掘，网络爬虫；9、除了JAVA外，精通另一门程序设计语言；10、紧跟技术发展方向，了解新技术，新开源框架；11、代码质量高，对于代码质量有一定的控制方法；12、有开发框架代码和组件代码的积累，能快速构建常见系统；13、会设计系统架构图和业务流程图。', '0', '2018-03-29 09:43:47', null);
