INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28419, '系统软件事业部-Docker/Kubernetes/CloudNative技术专家/高级技术专家-杭州/北京/深圳', '面议', '阿里巴巴', 'https://www.liepin.com/job/1912858986.shtml', '猎聘-其他', '5年以上', '北京', '本科及以上', '工作职责：1.参与集团内部或外部容器、调度相关的产品或项目的设计、研发，并推动落地2.参与Pouch、Kubernetes等开源项目的建设，扩大阿里在开源社区的影响力3.CloudNative思想布道，及相关前沿技术的调研与应用4.推动集团和学术界的交流合作，力争打造产学研一体的技术演进路线任职要求：1.精通Kubernetes，对Kubernetes设计理念及各模块源码比较熟悉，参与过源码贡献者优先2.精通Docker等容器引擎的使用，了解底层实现原理，并对容器网络、存储架构比较了解，熟悉OVS、Calico、Flannel等网络插件3.熟悉Linux平台，掌握Cgroup/CFS/CAT等资源隔离和调度的原理和用法4.深入理解CloudNative思想，并对行业发展趋势有敏锐的嗅觉5.三年以上JAVA或Golang开发经验，计算机相关专业优先考虑6.具备较好的逻辑思考能力、沟通能力、学习能力，工作中积极主动、有责任心、抗压性强', '0', '2018-03-29 09:44:19', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28420, 'Linux运维开发工程师（技术保障部）AS', '18-24万', '京东', 'https://www.liepin.com/job/1912855524.shtml', '猎聘-其他', '3年以上', '北京', '统招本科', '能力要求：·3年以上Linux系统运维开发经验。·深入理解Linux操作系统、熟悉Linux体系结构。·熟练掌握Linux操作系统常见命令，能够熟练开发Shell脚本。·熟悉程序部署、监控以及版本管理机制。·熟悉Linux资源隔离机制，熟悉KVM、Docker等虚拟化系统。·熟悉网络基础知识，熟悉常见网络协议（TCP/IP、UDP、ICMP等）、MAC地址、IP包、DNS、负载均衡等。·会熟练使用Java、Python、Go等一种或多种语言进行编程开发。·能够熟练使用SQL语句操作MySQL数据库。·良好的程序员素养，对工作积极主动，热衷用代码解决问题。工作职责描述：JDStack是京东公有云的拳头产品，本产品负责将京东公有云各产品线集成打包为可单独部署的模块，以极度灵活的方式服务客户。京东云技术保障部团队负责JDStack产品的设计、研发、集成与测试，这项工作充满了价值与技术挑战，在这里我们致力于用自动化的方式打通每一个环节，并采用科学严谨的工作方式提升团队产出，让每一位团队成员得到技术、业务的双重提升。在这里你可以体会到与京东一块成长的挑战与乐趣，并一起分享京东发展所带来的红利，我们时刻欢迎充满热情以及热衷技术的小伙伴们加入我们。', '0', '2018-03-29 09:44:19', null);
