INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28523, '糖猫-测试开发工程师-北京-01962', '面议', '搜狗', 'https://www.liepin.com/job/197564411.shtml', '猎聘-其他', '2年以上', '北京', '统招本科', '职位概述创新项目成长空间大，技术团队氛围佳~~~岗位职责1、根据产品合理安排测试计划，设计测试用例及测试数据，完成测试计划，并对测试结果负责；2、负责糖猫相关APP、糖猫手表软硬件，服务端测试等；3、根据项目需要完成自动化测试用例，白盒测试用例，性能测试脚本等的编写；4、根据项目需要，开发测试工具，监控工具等。任职条件1、正规高校计算机或相关专业本科以上学历，熟悉java、c++等任一种开发语言，具备扎实的专业基础知识（网络、数据库、软件工程等）2、熟悉手机应用，有客户端（ios，android）软件测试经验，有android或ios开发经验者优先，有OS测试经验优先3、熟悉软件测试流程，熟练使用缺陷管理工具和测试管理工具；4、具备良好的团队协作精神，良好的沟通能力、极强的责任心和学习能力。*(发布职位信息必填)特别提示:搜狗欢迎专情的你，所以提醒你只能选择两个项目，请慎重投递。部门概述糖猫是搜狗推出的一款能定位、可对讲的儿童智能手表，也是搜狗涉足智能硬件领域的第一款产品。糖猫本着将科技与生活相结合的理念，面向3~14岁各年龄阶段的儿童和青少年分别推出了相对应的产品系列，致力于为青少年提供更符合该年龄阶段的智能联网设备，和交流、娱乐平台。持续在智能硬件领域进行大量技术储备，并获得了数项技术专利。希望通过智能科技，让孩子的成长更快乐、更健康，让每一位家长都获得强大的超能力。', '0', '2018-03-29 09:44:48', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28524, '搜狗机器翻译-测试工程师-北京-02170', '21-42万', '搜狗', 'https://www.liepin.com/job/198742189.shtml', '猎聘-其他', '2年以上', '北京', '本科及以上', '【项目介绍】机器翻译项目是公司级重点项目。我们的目标是打破语言障碍，为13亿中国人提供优质的外语信息服务。目前我们已经搭建了自己的在线翻译,并全球首发跨语言检索,为用户提供高质量的英语搜索服务，同时将搜索结果翻译为汉语帮助用户理解。在单语世界到多语世界的发展潮流中，搜狗机器翻译技术正在迅速改变人们的工作和生活方式。【职位亮点】战略重点项目，强大的技术支撑、海量的翻译语料、高效精干的团队，满足亿万用户的跨语言检索需求。【岗位职责】1.负责翻译App客户端的测试工作，包括功能测试、数据容错、App性能、接口自动化、功能自动化等；2.根据产品及开发的需求，制定测试方案，设计测试用例，测试数据准备，测试执行，问题跟进等；3.提交、分析、跟踪软件设计缺陷，评估项目风险，保障产品质量，与开发团队进行有效沟通，推动解决测试中发现的问题；4.积极思考和发掘工作中的问题，可以独立自主完成测试工具的开发应用。任职条件1.正规高校计算机及相关专业，本科及以上学历，2年以上App客户端测试经验；2.英语6级，较强的英语听说读写能力；3.熟练使用Linux命令；熟练使用一种开发语言Python、Java或者objectc；4.熟练使用Appium、UIAutomator、Robotium、XCUITest自动化测试框架一种以上；5.熟练使用adb、monkey命令，了解ANR、CRASH、FPS、启动时间等概念；6.了解native、hybridapp的区别，理解C/S、B/S等开发架构，熟悉http、tcp原理；7.熟练使用抓包工具Fiddler进行网络数据分析；8.工作积极主动，细致、有耐心，能够独立思考和解决问题，有责任感，对结果负责；9.具备快速学习能力，优秀的沟通能力和团队合作精神，能快速地融入团队并与其他部门良好合作；', '0', '2018-03-29 09:44:48', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28525, '银河搜索-算法研发工程师-北京-02177', '28-56万', '搜狗', 'https://www.liepin.com/job/198769590.shtml', '猎聘-其他', '2年以上', '北京', '硕士及以上', '【项目介绍】银河广告是搜狗无线商业变现的重点项目，有别于传统的商业广告，银河广告旨在为用户推荐最符合其需求的信息服务。银河技术团队利用数据挖掘、机器学习、深度学习，NLP等技术在海量的客户广告中挖掘出最有价值的信息，团队鼓励技术创新，用前沿的技术快速解决有挑战的研发项目。【职位诱惑】商业重点项目、创新的技术氛围，定期技术分享、团队上升空间大【特别提示】搜狗欢迎专情的你，最多投递两个职位，请谨慎投递【岗位职责】负责搜狗银河广告的召回、展示策略等研发及算法优化，基于海量的用户数据，通过对机器学习、深度学习、数据挖掘、自然语言理解等前沿技术的创新与改进，不断推动业务与技术的飞速发展任职条件职位要求：1、计算机相关专业2、熟悉以下至少一个领域的主流算法并有实践经验：机器学习、自然语言理解、数据挖掘、计算机视觉、语音识别3、熟练掌握以下至少一门编程语言：C/C++/Java/Python，可以胜任算法的实现与开发4、对技术与研究有热情，愿意挑战新的算法和新的领域，对前沿研究领域保持敏感5、具有良好的沟通能力，和良好的团队合作精神满足以下条件优先：1、发表过机器学习或数据挖掘相关论文；2、熟悉深度学习，对CNN、RNN/LSTM、AttentionModel等模型有深入研究，掌握常用深度学习的框架并有实践经验；3、在GitHub等平台上深度参与或者建立有影响力的开源项目；4、有计算广告领域的实践经验，并对广告检索、广告排序、效果优化等模块有深入理解；5、有互联网公司实习经验；', '0', '2018-03-29 09:44:48', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28526, '搜狗金融-风控算法工程师-北京-02176', '28-56万', '搜狗', 'https://www.liepin.com/job/198770755.shtml', '猎聘-其他', '1年以上', '北京', '硕士及以上', '【项目介绍】这是一款定位于服务平台互联网用户的金融产品，由搜狗公司内部孵化，深度挖掘搜狗在互联网端的用户规模优势以及多年的大数据能力积累。在当下大热的互联网金融领域，搜狗将发挥用户服务和智能算法优势，并结合平台的综合运营实力。如果你认同互联网金融，智能风控，大数据模型等概念，欢迎加入我们共同创造事业。【职位诱惑】全新业务板块，可以接触到多面技术挑战，能够带来快速提高成长【特别提示】搜狗欢迎专情的你，所以提醒你只能选择两个项目，请慎重投递。【岗位职责】1、基于多维度数据的特征挖掘，生成；2、高精度机器学习模型开发，维护；3、提出模型用于业务的策略；4、模型结果检验，报告撰写；5、对业务问题设计合适的算法解决方案。任职条件1、硕士及以上学历，计算机、数学、金融学相关专业；工作经验丰富者可以放宽至本科。2、熟悉Python，linux，shell编程。3、熟悉机器学习建模流程。4、熟练使用pythonsk，pandas，numpy，xgboost。5、熟悉boosting，ensemble原理以及二次封装算法。6、深入理解各种常用基本机器学习算法（回归、分类、聚类）。符合以下条件一条或多条优先考虑：1、在kaggle，天池，或国内外其他公开竞赛中取得5%以上排名。2、熟练使用tensorFlow/keras/dl4j其中之一进行深度学习建模。3、熟悉网络交易、信贷反欺诈业务;4、熟悉企业、个人征信业务；5、有个人技术博客或在各技术社区活跃;6、熟悉java语言，Hadoop/Hive/spark等分布式计算和存储;7、有规则引擎，数据建模分析经验;8、有机器学习工具使用经验;9、有海量数据模型分析的项目经验；', '0', '2018-03-29 09:44:49', null);
