INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28530, '蚂蚁金服-（TIP）数据挖掘专家-北京', '面议', '阿里巴巴', 'https://www.liepin.com/job/1912656267.shtml', '猎聘-其他', '5年以上', '北京', '本科及以上', '工作职责：基于海量数据，进行各类数据挖掘模型和算法研发，并负责在生物识别业务中落地和产生应用价值，提升用户使用体验和安全性。任职要求：1.5年以上数据挖掘和建模经验，熟练掌握各种数据挖掘与机器学习算法，包括分类、聚类、深度学习等。对算法原理、实现、优化有较深入了解。2.至少精通以下技术和应用中的至少一种：（1）逻辑回归、随机森林、深度学习等分类模型和算法及其在安全风控中的应用；（2）强化学习算法和技术，及其在智能优化和决策中的应用。3.熟悉Hadoop等分布式框架，能熟练进行MapReduce开发。4.精通至少一种开发语言，如：Java、Python，能熟练使用SQL进行数据处理。5.良好的沟通表达能力，优秀的学习能力，能够快速掌握工作所需的新技能，能承受必要的工作压力。6.良好的英语听说读写能力，能够以英语进行日常工作沟通。', '0', '2018-03-29 09:44:50', null);
