INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28549, '网页搜索-资深高级测试开发工程师-北京-03854', '38-75万', '搜狗', 'https://www.liepin.com/job/1912802913.shtml', '猎聘-其他', '5年以上', '北京', '本科及以上', '【项目介绍】网页搜索项目，承载着PC及WAP的网页搜索，直接服务数亿中国网民。在这里有深厚的技术积淀，有浓郁的技术氛围，可以学到很多核心的技术。在这里，我们还可以一起探索社交搜索和知识图谱，让搜索变得更有趣与更智慧。同时，我们也拥有很多创新的机会，为用户提供更好更有趣的搜索体验。【职位诱惑】推荐算法在互联网领域有极广泛的应用；基于搜狗的海量数据，推荐算法研究具有得天独厚的优势；团队氛围融洽，学习、讨论氛围浓厚。涉及产品线包括：新闻推荐；搜索推荐；推荐插件等；以提升产品效果为目标，进行推荐算法研究和应用。【岗位职责】1.从事搜索核心组件的集成测试。2.负责3-5人测试开发团队管理，项目进度的整体协调。3.根据项目需求自研、维护自动化平台。【任职条件】1.有5年以上linux服务端测试经验，3年以上团队管理经验。2.熟练掌握C++和java，熟悉linux上通用代码调试方法3.能够开发socket连接测试工具，使用多线程提高工具并发度。4.可以使用memcache、hadoop、redis等至少一种第三方系统搭建服务。5.熟悉linux自动监控平台的搭建。6.熟悉业界常用测试框架，独立搭建并且使用过至少一种测试框架。7.熟练使用C++代码覆盖率工具，有白盒试经验。8.计算机相关专业本科及以上学历。', '0', '2018-03-29 09:44:57', null);
