INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28791, 'Java开发工程师', '10001-15000元/月 ', '中国新闻出版研究院', 'http://jobs.zhaopin.com/387439011250329.htm', '事业单位', '不限', '北京', '本科', '工作职责:1. 参与知识资源服务平台门户网站开发；2. 参与试点单位案例对接集成，保证项目质量；3. 完成系统在Linux上部署运维工作；4. 根据具体的模块需求，独立完成模块的设计编码工作；5. 记录开发文档，接口文档；6. 其他技术相关工作任务，领导安排的其他工作事项。职位要求:1. 大学本科以上计算机相关专业；2. 3年以上相关工作经验, 全程参与至少一个完整项目的开发；3. 良好的技术基础,精通JAVA语言的编程,精通SpringMVC+MyBatis开发框架和http接口开发,熟练使用Jsp；4. 掌握idea、eclipse、maven、svn、git等开发工具；5. 熟练Linux环境以及操作，熟练Tomcat，Apache，Nginx等应用的集群部署工作；6.有较强的算法基础和编码能力； 对数据敏感，掌握JAVA的系统开发，熟悉Linux下shell编程，至少掌握一门脚本语言；7. 精通SQL语言，熟悉主流数据库，MySQL或Oracle, 有调优经验；8. 熟悉目前主流的Redis、memcached等缓存框架；9. 工作态度认真，积极主动，具有良好的语言表达和沟通能力，有良好的团队合作精神，有独立工作能力。加分项:1.有lucene,solr,elasticsearch ,搜索引擎、推荐系统等相关的项目经验优先；2. 有hadoop,word2vec,spark,MLlib大数据挖掘、机器学习等方面实际项目经验者优先，或有浓厚的兴趣，有很强的自学能力；3. 有过知识服务系统，相关开发经验者优先。', '0', '2018-03-29 17:10:15', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28794, 'JAVA开发工程师', '10001-15000元/月 ', '中融集团', 'http://jobs.zhaopin.com/137005291250680.htm', '民营', '3-5年', '北京', '本科', '岗位职责：1、在架构师的指导下进行系统核心模块设计、开发；2、参与产品原型及数据模型等设计；3、协助项目经理制定开发计划及开发质量管理；4、编写公司相关项目技术方案及文档.任职要求：1、学历要求：本科及以上学历，计算机、软件工程相关专业；2、专业要求：（1）熟练使用各种开发工具（Eclipse/MyEclipse、Maven、Junit、Svn）；（2）精通SSH和SSM原理和使用方法；（3）熟练使用JavaScript脚本语言，熟悉JQuery类库；（4）可以独立完成简单模块的数据结构设计和代码开发；3、经验要求：3年以上项目开发经验，有大型金融项目开发经验者优先；4、其他要求：良好的沟通能力与团队协作能力，思路清晰，有独立解决问题的能力。', '0', '2018-03-29 17:10:19', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (28825, '京东物流云-java开发工程师', '面议 ', '京东商城', 'http://jobs.zhaopin.com/192921312311217.htm', '外商独资', '3-5年', '北京', '不限', '职位描述：1、 参与公司JAVA后台服务的开发；2、 完成项目的核心编码，根据开发计划，保证系统的开发进度和质量；3、 负责完成相关文档的撰写及部门交办的其他任务。任职资格：1、计算机相关专业毕业，本科及以上学历，2-5年工作经验；2、扎实的Java编程基础，深入理解面向对象设计、设计模式，以及一些常用数据结构算法；3、熟悉大规模分布式系统的相关运行机制并有实际开发经验，掌握高并发下系统的高可靠，高可用机制；4、熟悉Oracle/Mysql/SqlServer其中一种数据库，熟悉weblogic,Tomcat,Jetty,websphere其中一种服务器；5、熟练掌握常见开源框架和技术，如 ibatis、SpringMVC、Redis、jQuery、ActiveMQ等；6、喜欢尝试最新的技术，从技术趋势和思路上有自己的见解；7、思维缜密，责任心强，具有较强的沟通能力及团队合作精神；8、熟悉仓储业务及有相关开发经验的优先。', '0', '2018-03-29 17:11:10', null);
