INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (29523, '自然语言处理工程师', '15001-20000元/月 ', '中国新闻出版研究院', 'http://jobs.zhaopin.com/387439011250322.htm', '事业单位', '不限', '北京', '硕士', '职位描述：1.	负责分词、词性分析、实体识别、新词发现等工作；2.	文本挖掘以及知识体系和知识图谱构建；3.	负责查询分析/查询改写/查询意图识别等工作；4.	应用NLP数据挖掘算法解决搜索相关问题；职位要求：1.	有较强的算法基础和编码能力； 对数据敏感，掌握JAVA的系统开发，熟悉Linux下shell编程，至少掌握一门脚本语言；2.	熟悉中文分词、非结构化文本提取、语义分析、语义表示、语义匹配、组块分析、主题模型、篇章分析、情感分析、词向量技术、命名实体识别等基本算法和应用；3.	有lucene,solr,elasticsearch ,搜索引擎、推荐系统等相关的项目经验优先；4.	有hadoop,word2vec,spark,MLlib大数据挖掘、机器学习等方面实际项目经验者优先，或有浓厚的兴趣，有很强的自学能力；5.	计算机或相关专业大学硕士或以上学历。', '0', '2018-03-30 20:14:03', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (29559, '数据挖掘工程师(017132)(职位编号：JD017132)', '面议 ', '京东金融', 'http://jobs.zhaopin.com/19292131290295271000.htm', '外商独资', '5-10年', '北京', '本科', '岗位职责:1、基于京东体系海量数据，利用算法挖掘数据内在的模式和特征，发现潜在规律，建立数据挖掘算法并优化；2、负责将算法与海量的实际业务数据结合，并持续维护算法的有效性；3、基于挖掘结果总结有价值信息，为业务决策、管理等提供数据支持；4、支持数据分析师的数据模型工作。任职资格:1、计算机、统计学、数学、数理统计等相关专业，本科以上学历，3年以上相关工作经验；2、熟练掌握一种编程语言R，C/C++、java、python、perl等；3、熟悉Hadoop 等至少一个分布式框架，了解MapReduce基本原理，具备分布式平台处理海量数据的项目经验；4、对统计学和数据挖掘算法原理有较为深刻的理解，熟悉决策树、聚类、逻辑回归，关联分析、SVM，贝叶斯等数据挖掘算法优先；5、有用户行为分析、用户建模、业务建模、数学建模经验优先；6、良好的逻辑分析能力、分析问题和解决问题的能力，对数据敏感，自我学习驱动能力强，较强责任心；7、具备良好的团队协作意识、时间意识、沟通能力，能够承担工作压力。更多职位动态，敬请关注微信公众号：京东金融招聘。', '0', '2018-03-30 20:14:55', null);
