INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (29646, '高德-安全研发&数据算法专家(职位编号：GP054402)', '面议 ', '阿里巴巴集团', 'http://jobs.zhaopin.com/000127917292229.htm', '合资', '3-5年', '北京', '本科', '岗位描述：1) 负责高德网络安全、数据安全、应用安全、业务安全、生态安全等安全产品的研发和底层数据算法工作；2) 负责完整的软件开发生命周期（共创、需求分析、设计、开发、测试、发布上线）；3) 在具备出色的算法和系统能力基础上，深入了解高德安全的复杂业务，并针对业务的核心问题能系统进行数据算法的系统规划、具体落地，运用数据算法能力推动复杂业务问题的解决，赋予安全业务的数据化意识与能力。岗位要求：1) 精通Java、C、C++语言，有五年以上开发经验；2) 熟悉TCP/IP，及常见网络协议(例如HTTP等)；3) 有一定的安全领域背景，有安全产品研发和安全业务场景下算法工作经验者优先；4) 具有互联网大数据工作经验，特别是具有企业级风控领域大数据工作经验者优先；5) 具有异常检测算法实战经验者优先，在用户行为方面使用过深度学习算法者优先；6) 具备良好的沟通表达能力，一定的项目管理能力，良好的团队合作意识；', '0', '2018-03-31 20:46:06', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (29678, 'web开发工程师', '26-52万', '京东', 'https://www.liepin.com/job/1912681285.shtml', '猎聘-其他', '5年以上', '北京', '本科及以上', '岗位职责:1.负责B/S管理系统及业务系统的前端开发工作。2.Web前端表现层、页面制作、交互开发及后端（Java）联调。2.JavaScript程序模块开发，通用类库、公共方法的编写。3.与设计师、开发工程师紧密工作，负责产出高质量的产品前端层。4.通过各种前端技术手段，提高用户体验并满足性能要求。5.能充分理解项目需求和设计需求，具有一定钻研精神，能解决各种未知问题。任职资格:1.计算机或相关专业专科以上，3年+前端开发经验。2.掌握JS语言核心技术DOM/Ajax等，对JS框架（jQuery/Bootstrap/Vue/React等）有实际使用经验。3.对CSS/JS性能优化、解决多浏览器兼容性问题有一定的经验。4.对用户体验、交互操作流程、及用户需求有一定了解。5.具备良好的服务意识、责任心、较强的学习能力、优秀的团队沟通与协作能力。', '0', '2018-03-31 20:46:58', null);
