INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (30749, '数据库事业部-OLAP产品架构师-杭州/北京', '面议', '阿里巴巴', 'https://www.liepin.com/job/1913270504.shtml', '猎聘-其他', '5年以上', '北京', '本科及以上', '工作职责：我们是阿里巴巴集团数据库事业部OLAP平台团队，我们专注于提供全球领先的全栈式大规模OLAP产品，目前在阿里集团、公共云、专有云提供了分析型数据库AnalyticDB、时间序列数据库HiTSDB等多个产品，得到了大规模应用。产品致力于用低成本、高性能的方案满足大数据时代的实时在线分析需求，帮助客户快速挖掘数据价值。职位描述1.深度挖掘OLAP客户以及市场的需求，关注竞争对手的产品更新，有效的规划产品迭代节奏，保障产品有长远的核心生命竞争力。2.参与具体的技术架构和产品架构把关，保障产品需求有效落地。3.能够分析产品阶段性特征，针对不同阶段，指定不同的产品架构以及业务打法。持续有效的推进产品向前演进。4.负责产品的商业化以及头部客户的运营管理。有效的将市场和客户需求与研发线很好的结合起来。5.利用数据化持续推进产品的服务体验提升、成本优化。6.负责产品上下游和周边生态的建设，以及在行业内影响力的运营。任职要求：1.熟悉大数据基础架构（采集、传输、存储、计算到调度），参与过企业客户的数据仓库建设者优先。2.深入理解mpp技术以及数据分析SAAS方案，对传统的OLAP架构OracleRAC、Greenplum、ApacheHAWQ熟悉者优先。3.熟悉Java编程，对数据比较敏感，有一定的数据分析能力，能够利用数据不断推进优化。4.熟悉GPU加速、或者机器学习者优先。5.加分项：有传统ETL建设或者开发背景、数据库DBA、熟悉Hadoop/Hive/Hbase/Spark/Beam/Yarn/HANA/GreenPlum/Oracle之一', '0', '2018-04-04 22:40:37', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (30751, '优酷-高级前端开发', '面议', '阿里巴巴', 'https://www.liepin.com/job/1913269841.shtml', '猎聘-其他', '3年以上', '北京', '本科及以上', '工作职责：1.支撑企业级应用后台开发，使用React等前端框架搭建后台页面，实现交互需求及后端服务对接;2.以模块化的思想设计开发通用前端组件，并能够针对OTT,移动端进行针对性优化；3.在理解前端开发流程的基础上，结合前端实际建立或优化提升工作效率的工具；4.在理解产品业务的基础上，提升产品的用户体验，技术驱动业务的发展；5.关注前端前沿技术研究，通过新技术服务团队和业务；6.使用Weex技术开发产品需求。"任职要求：1.精通前端技术，包括HTML/CSS/JavaScript/Node.JS等；2.掌握Bootstrap，jQuery，AngularJS，React等框架，并有项目实践；3.熟悉前端模块化、编译和构建工具，如grunt，gulp,webpack等；4.至少熟悉一门非前端的语言（如Java/PHP/C/C++/Python/Ruby）,有项目实践更佳；5.具备跨终端的前端开发能力，在Web（PC+Mobile）/Node.js/NativeApp三个方向上至少精通一个方向，具备多个的更佳，鼓励在Native和Web技术融合上的探索；6.具有较强的学习能力，对前端技术有持续的热情，个性乐观开朗,逻辑性强，善于和产品，UED，后端等方向同学合作。', '0', '2018-04-04 22:40:38', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (30759, '应用工程师（应用迁移及运维方向）', '18-24万', '首都信息', 'https://www.liepin.com/job/1913264225.shtml', '猎聘-其他', '5年以上', '北京', '本科及以上', '岗位职责：1.负责云计算平台相关应用的迁移和运维工作，包括基础架构系统设计、技术实施和运维2.与合作商一起制定迁移系统的需求调研、云架构设计、迁移计划、时间表3.与相关团队密切配合，优化部署架构、提升产品性能、定位技术问题，确保所有产品的高质量、高效率和高可靠性4.参与项目方案制定并提供相应的解决方案，能够及时处理突发事件5.保障业务服务器稳定的运行，对业务系统进行优化与故障处理，提升系统性能和稳定性6.对解决方案进行文档化工作7.负责部分云产品服务方案讲解、交流引导、文档编写、技术咨询8.参与部分云产品服务的产品选型工作；完成领导交办的其它工作任职资格：1.计算机本科及以上学历，3年以上大中型在线系统开发或运维工作经验2.深入理解Linux系统，有系统调优经验者优先考虑3.熟悉一门编程语言（shell、python、java等均可）4.熟练掌握Tomcat、Nginx、Apache、Lighttpd、squid、memcached或redis、varnish、HAproxy等常用软件的安装,配置和管理5.能够熟练排查运维过程中出现的服务故障、系统故障6.高度的责任感，较强的故障分析及排除能力，善于在工作中学习，能够承受工作压力7.对行业技术敏感度高且细致，善于思考，乐于发现，乐于助人，对解决具有挑战性问题充满激情8.对云计算相关技术有了解；有较强的沟通和协调能力9.熟悉ITIL开发运维流程的和政府相关应用开发运维工作的优先考虑', '0', '2018-04-04 22:40:43', null);
