INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (30765, '测试开发工程师', '21-42万', '京东', 'https://www.liepin.com/job/1913262145.shtml', '猎聘-其他', '4年以上', '北京', '统招本科', '职责描述：1、根据项目计划制定测试用例并实施，保证项目质量和进度；2、跟踪定位产品项目中的缺陷或问题，与项目相关人员就项目进度和问题进行沟通；3、参与程序架构和代码的评审工作，并提出改进意见和可测试性建议；4、根据项目设计与实现有关自动化测试的代码与用例；任职要求：1、工科、计算机或其他相关专业本科以上学历，至少4年以上软件测试经验；2、精通软件测试理论、方法和过程，熟悉常用的测试策略、测试用例设计方法等；3、熟悉Linux或Unix操作系统；4、熟悉Oracle/Sqlserver/MySQL等常用数据库系统的操作，能熟练编写SQL语句；5、掌握常见的Web测试工具，如Selenium、Jmeter等；6、至少掌握一门编程或脚本语言，如Java、Python等；7、对软件测试有浓厚的兴趣和丰富的经验，有很强的分析能力和定位问题的能力；8、有很强的责任心，做事严谨，有良好的的沟通、团队协作意识及多任务协调能力。', '0', '2018-04-04 22:40:48', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (30769, '菜鸟-数据算法专家-数据团队', '面议', '阿里巴巴', 'https://www.liepin.com/job/1913260757.shtml', '猎聘-其他', '5年以上', '北京', '本科及以上', '工作职责：对菜鸟物流生态数据进行挖掘、分析，通过对复杂业务或系统问题进行建模，从数据和算法上研究对物流生态的效率提升和成本下降的方法，工作内容可能包括但不限于：数据挖掘、运筹优化、机器学习、仿真系统等。任职要求：1)统招本科或以上学历，计算机、数据挖掘、统计学、运筹学、机器学习、文本挖掘或其他相关专业2)丰富的算法设计开发或数据分析技能和相关经验，具备较好的英文阅读能力3)熟悉Java/Python等编程语言，熟悉Hadoop,spark,Hive等大数据处理工具4)优秀的团队合作精神、诚实、勤奋、严谨；有以下经验者优先：供应链／物流行业背景，发表高水平算法相关论文', '0', '2018-04-04 22:40:50', null);
