INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (30806, 'GO高级开发', '28-42万', '京东', 'https://www.liepin.com/job/1913219367.shtml', '猎聘-其他', '3年以上', '北京', '统招本科', '职责描述：1、参与serverless架构体系系统及Faas平台相关系统的设计开发工作。2、根据项目需求、用户需求对系统及功能做出合理的、有预见性的设计。3、跟进业界的同类产品和技术的发展，结合京东技术特点和业务特点，转化技术应用到实际工作中。4、对线上系统进行维护，处理技术问题和性能问题，不断提升系统品质。5、协同上下游技术团队完成Faas平台生态体系的建设，为系统最终用户的使用提供支持。任职要求：1、至少3年以上的go开发经验2、熟悉Web服务端、中间件、基础组件此类开发工作3、熟悉分布式系统架构及相关技术，如缓存、消息队列、RPC、数据库(Sql/Nosql)、http服务等4、具备一定的设计能力和解决线上问题的经验和能力5、熟悉linux，有一定运维能力6、熟悉k8s、dockerfile、docker者优先7、对高并发、高可靠，资源调度场景有实际经验者优先8、研读过开源项目，做过二次开发改造者优先9、多语言能力者,如java、python（加分项）10、从事过Faas，云中间件，云基础服务开发工作（加分项）11、善于沟通和协作，有团队精神和自我驱动能力', '0', '2018-04-04 22:41:16', null);
