INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (30995, '研发效能事业部-研发协同平台-高级技术专家-软件编译/代码托管-杭州，北京', '面议', '阿里巴巴', 'https://www.liepin.com/job/1912569076.shtml', '猎聘-其他', '5年以上', '北京', '本科及以上', '工作职责：阿里巴巴研发效能事业部属于CTO线九大核心事业部之一，目前由阿里巴巴研究员毕玄(bluedavy)大牛带领。研发协同平台是研发效能事业部的核心产品和业务，我们打造了一站式智能研发协同平台，包含项目管理、代码托管及服务、测试及持续集成、持续交付、度量和运营等领域，为阿里几万产品研发相关人员提供工具支撑，通过将优秀管理理念和工程实践落地到平台，理念*工具*实践相结合提升工程效率和组织效能。同时通过阿里云对外输出开放给外部客户使用，是阿里云上重要的业务之一，为全球各地企业和开发者提供软件生产运营服务，阿里巴巴集团的生态系统为我们的产品提供了丰富的场景，“WorkLikeAlibaba”将成为中国及至全世界开发者最潮流的口号。本职位主要职责是研发我们的云服务平台及相关工具，我们有着一群懂协作有技术有梦想的工程师，一起解决大型项目协作、敏捷高速迭代、海量代码托管、高效测试工具、分布式秒级构建、大规模集群部署发布等世界级业务和技术难题，为阿里巴巴集团内部、生态伙伴以及云上开发者服务。我们的使命：提升研发效能，让协作更简单，让交付更高效我们的愿景：打造世界一流的软件生产运营梦工场1.负责公司及阿里云上研发协同平台的架构和核心代码研发；2.负责专项工具技术的深入优化，如代码托管、测试工具、软件编译优化等；3.参与云端开发工具链建设,打造类似google工具开发基础设施;4.攻克各种高并发、数据隔离、系统解耦、云解决方案等方面的技术难关；5.参与产品的部署运维，保障系统高可用性任职要求：1.本科以上学历，工科、计算机或其他相关专业优先；2.云端开发工具链有领先的理念、全局规划和推动落地能力，具有扎实的c++/java编程和设计能力3.在代码托管、测试工具、软件编译优化等专业技术方面有深厚的经验积累，能通过技术深入优化大幅提升效率4.云产品技术架构能力(公共云、专有云、混合云)5.职位base地可为：北京，杭州，美国', '0', '2018-04-06 19:40:02', null);
