INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (31010, '卢米埃-测试工程师', '12-24万', '爱奇艺', 'https://www.liepin.com/job/1912541369.shtml', '猎聘-其他', '1年以上', '北京', '本科及以上', '工作内容：1、分析产品需求，根据需求编写测试用例2、负责产品功能检测和发布，以及自动化测试工具的开发3、负责App终端体验和系统性能评估，持续优化策略和测试方法任职要求：1、一年以上测试相关经验，有互联网行业工作经验优先2、熟练掌握基本的测试理论，可以准确将需求文档中各个功能点转化为测试点并编写测试用例3、能够发现产品的缺陷与问题，并能定位区分前后端的问题4、熟悉基本的UNIX/Linux操作及SQL语句5、熟悉Java或Python，有自动化测试以及开发经验者优先考虑。6、有强烈责任心，积极思考，有良好的协作沟通能力和团队合作精神，对改善产品质量以及团队工作流程有着强烈的热情。', '0', '2018-04-06 19:40:24', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (31011, '安全开发工程师', '18-36万', '京东', 'https://www.liepin.com/job/1912537726.shtml', '猎聘-其他', '2年以上', '北京', '统招本科', '岗位职责：1.负责京东云安全自动化平台、安全管理系统开发，实现安全日常工作的自动化；2.负责安全监控数据、系统日志及业务日志的数据分析平台开发；3.负责内网异常流量监控、漏扫、基线、情报等系统的部分开发工作；4.深入开源安全系统的调研、定制开发，并推进内部应用实践；5.持续优化系统架构，提供更准确、更稳定、更可靠的安全系统；任职要求1.对安全技术感兴趣，拥有两年以上的开发经验；2.熟练C/C++/PHP/Perl/Python/Shell/Java至少1种编程语言；3.熟悉各种开源组件的使用，包括但不限于Redis，MQ，MongoDB，Git，Tomcat等；4.熟悉网络安全、OS安全、应用安全、数据安全中的一种；5.有扎实系统知识储备，有多线程、高并发、分布式架构设计等相关经验;6.熟悉OWASPTOP10常见WEB安全漏洞原理和修复方案。', '0', '2018-04-06 19:40:25', null);
