INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (31033, '高级数据挖掘工程师', '面议', '京东', 'https://www.liepin.com/job/1912614625.shtml', '猎聘-其他', '6年以上', '北京', '本科及以上', '岗位职责：1、基于京东体系海量数据，利用算法挖掘数据内在的模式和特征，发现潜在规律，建立数据挖掘算法并优化；2、负责将算法与海量的实际业务数据结合，并持续维护算法的有效性；3、基于挖掘结果总结有价值信息，为业务决策、管理等提供数据支持；4、支持经营分析师的数据模型工作。岗位要求：1、计算机、统计学、数学、数理统计等相关专业，本科以上学历，3年以上相关工作经验；2、熟练掌握一种编程语言R，C/C++、java、python、perl等；3、熟悉Hadoop等至少一个分布式框架，了解MapReduce基本原理，具备分布式平台处理海量数据的项目经验；4、对统计学和数据挖掘算法原理有较为深刻的理解，熟悉决策树、聚类、逻辑回归，关联分析、SVM，贝叶斯等数据挖掘算法优先；5、有用户行为分析、用户建模、业务建模、数学建模经验优先；6、良好的逻辑分析能力、分析问题和解决问题的能力，对数据敏感，自我学习驱动能力强，较强责任心；7、具备良好的团队协作意识、时间意识、沟通能力，能够承担工作压力。', '0', '2018-04-07 19:50:22', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (31039, '算法工程师', '23-52万', '京东', 'https://www.liepin.com/job/1911334636.shtml', '猎聘-其他', '3年以上', '北京', '硕士及以上', '职责描述：1.搭建超大规模检测、识别、分类系统。2.基于强化深度学习进行模型优化。3.通过数据挖掘制定科学运营计划。4.数据驱动提升工作作业效率。5.基于运筹学的工作模型优化。任职要求：工程能力或算法能力突出者优先1.有在大数据平台上处理大规模实时数据及应用机器学习模型经验者加分（spark，tensorflow）。2.掌握常用算法编程语言，如Python,C++,R,java,scala等。3.有信息搜索和文献阅读及算法实现的能力。4.掌握常用运筹学，自然语言处理，机器学习算法，具备数学建模的能力。5.熟悉统计语言，如R,或S，熟悉数据库及SQL。6.在机器学习模型和算法方向有应用经验。基于分布式及大数据平台的优先考虑。7.在统计/数学/计算机/机器学习/运筹优化相关领域拥有研究生及以上学历。对数据敏感，喜欢并善于从数据中发现规律。', '0', '2018-04-07 19:50:28', null);
