INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (31738, '云产品研发架构师', '20001-30000元/月 ', '中国电信集团系统集成有限责任公司', 'http://jobs.zhaopin.com/000520811251238.htm', '国企', '10年以上', '北京', '本科', '【公司简介】中国电信集团系统集成公司专注电信ToB业务，对内负责中国电信的大型系统的研发与顶层的业务方向的技术支持与搭建，对外为电信的政府和企业客户承接建设大型的信息化项目。公司从电信长途资源管理、智慧光网、大客户网管、拨测、计费等行业应用；天翼对讲、绿色军营等移动互联网应用；云灾备服务平台；天翼信云、天翼云服等云计算信息安全产品；到研发了电信云管理平台、电信大数据飞龙平台、电信开源比翼共享研发平台等一系列大型软件著作专利产品，包括代表中国电信申请了Openstack黄金会员等尖端技术荣誉。公司连年业务增长率30%以上，在全国有32家分公司、1家子公司与4个区域中心定位安全智能的云网平台服务领导者，战略愿景：中国电信DICT时代主力军，助理工业及信息变革。【岗位内容】本次集成总部招聘主要针对公司云计算与灾备服务事业部重点业务单元与产品服务诚聘英才，岗位如下：云产品架构师岗位职责：1、负责分析、设计和搭建公司云产品的系统架构，解决开发中各种架构及技术问题；2、负责云产品核心框架及功能的开发；3、诊断和解决现行系统可能存在的问题，保证公司系统的安全、稳定、高效率运行；4、制定相应的设计规范和开发规范；5、 负责组织与项目相关人员共同完成模块设计与开发；6、 按照项目计划,在保证质量的前提下按时完成开发任务；7、 维护和升级现有产品，快速定位并修复软件缺陷；8、 与项目相关管理、测试和需求等人员沟通和合作；9、 负责相关技术文档的编写。10、负责组织研发团队，配合运维团队保障运营平台的稳定，解决各类技术问题岗位要求：1、精通java预研，对JEE有深入理解，熟悉JEE规范，有3年以上开发经验，有良好的代码编写习惯；2、掌握至少两种以上主流框架开发技术，SSH、Spring MVC、ASP.NET MVC等均可；3、掌握至微服务框架开发技术，Spring Boot 、Spring Cloud 等；4、精通Java/python WEB项目开发，熟悉jsp、Jquery、JavaScript/Ajax；5、熟悉HTML、CSS、javascript等Web前端开发技术；6、掌握至少两种以上主流数据库，Oracle、MySQL、MongoDB等均可；7、熟悉restful API webserver的原理，熟练掌握django或flask等python web框架进行开发；8、具有较强的沟通能力，良好规范的编程习惯和技术文档编写习惯，熟悉软件开发设计思想；9、具备较强的逻辑分析及学习能力，有良好的团队合作意识，有强烈的责任心和积极主动的工作态度；10、对新技术持有敏感性，愿意致力于新技术的探索和研究 ；11、能够指导他人完成或独立完成系统的架构设计、开发、测试和部署；12、带领过5人以上的开发团队，完成项目的开发设计、核心和框架代码编写，组织和安排开发人员日常工作', '0002-01-01 00:00:00', '2018-04-09 22:05:56', 1);
