INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (31950, '架构规划设计专家', '面议 ', '中国联通', 'http://jobs.zhaopin.com/271309317250365.htm', '其它', '不限', '北京', '不限', '岗位职责1、精通新产品、新技术的架构规划设计（云计算、大数据、人工智能、物联网和其他主流技术领域）2、理解客户在研发、运营和业务工作中对网络、计算、存储等基础设施、数据治理的需求3、为核心客户提供基于公司各产品整体方案的售前和咨询工作4、确保重点项目的落地，推进项目实施以及解决方案可复制，推动跨部门合作5、了解行业技术发展变革方向，能结合IT发展趋势在行业方案上进行前瞻性布局。岗位要求1、在所服务过的企业中已经作为架构规划设计带头人的成熟且成功的经验。2、对服务端开发，精通Java/Scala/Python/PHP/C/C++等开发语言以及对应开发框架有深入研究。精通openstack等虚拟化架构及主流公有云服务，并能够清晰描述其工作原理和基础的架构逻辑(Iaas, Paas, Saas)，对AWS、腾讯云、阿里云等不同品牌的云服务区别；3、有海量业务的架构设计、研发、运营及优化经验；4、具有丰富的企业级售前咨询或云服务集成实施经验；', '0', '2018-04-10 17:40:28', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (31951, '大数据平台工程师', '面议 ', '中国联通', 'http://jobs.zhaopin.com/271309317250380.htm', '其它', '5-10年', '北京', '本科', '岗位职责1.具备大数据基础平台的建设能力，参与整体规划和架构设计，参与需求分析、架构设计、技术决策以及详细设计。2.精通Hadoop/Hive/Spark/Yarn使用，精通源代码，带领团队具备更改源代码能力。对深度学习、统计机器学习等有一定的研究。3.需要较强的逻辑分析，需求分析能力，能够协同团队推动业务的发展。岗位要求1、本科及以上学历，计算机相关专业，6年以上开发经验；2、精通Java开发，熟悉IO机制、网络通讯、多线程等基础知识框架，熟悉缓存、消息队列、索引查询等机制；3、熟练掌握Hadoop的MapReduce应用的开发以及部署，熟悉分布式计算系统理念，熟悉Hive、Pig等大数据开发工具；4、熟练掌握linux、python，熟悉spark框架、storm流式计算5、对数据库有一定了解，熟悉ETL流程；6、熟悉推荐系统，掌握各种数据挖掘算法者优先；7、有爬虫、网页正文提取、分词和特征提取等相关经验优先；', '0', '2018-04-10 17:40:29', null);
