INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (32342, '22989-腾讯云大数据产品研发专家（北京）', '面议', '腾讯', 'https://www.liepin.com/job/1913396771.shtml', '猎聘-其他', '8年以上', '北京', '本科及以上', '工作职责：负责腾讯云大数据平台架构的规划和设计；参与腾讯云大数据平台和基础产品的开发和改进，保证数据平台的稳定和可靠；持续优化云上大数据平台的性能调优，支持海量数据的离线和实时分析，改进多维数据仓库产品的设计与设计，保证在海量数据下，查询结果能高效快速输出；关注大数据方向开源产品的技术动态，参与开源产品的技术演进，推动平台产品与技术架构持续更新。任职要求：计算机、通信等相关专业，本科及以上学历，8年以上大型互联网产品或分布式系统开发设计经验；精通和深度参与Hadoop/Spark/Hive/Presto/HBase/Kylin/Impala/Kafka/Flink/Storm/Redis等主流大数据产品中至少一项产品的设计与实现，并对多项产品有丰富的使用、部署以及trouble-shooting的经验；熟悉和掌握常用的分布式系统的原理，包括在实践中深入理解CAP理论以及BASE设计理念；有OLAP，多维度数据分析经验优先，有较丰富的数仓使用经验，对数据仓库有较强的理论基础和理解；热爱大数据技术，认同数据价值，思考有深度和体系，对大数据技术发展趋势有自己独到的观点；沟通能力强，有较强的技术领导力，能够激励并带领团队完成产品开发上所面临的技术挑战；精通Java,C++,Scala等语言中的一种或几种，并熟悉Shell或Python等一种或几种脚本语言，熟悉Linux/Unix环境。', '2018年04月11日', '2018-04-11 18:18:55', null);
