INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (32751, '高级Java开发工程师', '15001-20000元/月 ', '北京火币天下网络技术有限公司', 'http://jobs.zhaopin.com/620797421250464.htm', '民营', '不限', '北京', '本科', '岗位职责：1、互联网金融平台等产品的架构规划及核心功能的开发；2、参与产品规划、方案制定、需求调研和架构设计；3、指导项目团队成员的日常开发工作，解决开发中的技术问题。任职要求：1、计算机相关术专业，本科及以上学历；2、有金融相关产品研发经验，或一二线大型互联网公司工作经验；3、有良好的编码习惯，能够改善编码规范，降低软件维护成本；4、能够承担较强的工作压力，保证所负责相关领域工作进度及业务支持；5、熟悉MVC，熟悉JVM，熟练使用springboot、SpringMVC、ibatis等相关开源框架进行项目开发；6、熟悉Mysql数据库使用，熟练事务等机制以及熟悉数据库优化；7、熟悉Linux操作系统，能够编写脚本及配置相应Java运营环境，熟悉基于Linux平台和Java容器的负载均衡方案，熟悉数据共享、同步等的实现方案；8、熟悉主流Web应用服务器JBoss、Tomcat、WebLogic、缓存服务器、优化配置与使用；9、熟悉Linux、Apache、Tomcat等应用服务器的应用部署和配置。', '0002-01-01 00:00:00', '2018-04-13 17:32:43', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (32752, '系统集成经理/系统集成高级工程师', '1-1.5万/月', '上海云砺信息科技有限公司', 'https://jobs.51job.com/beijing-hdq/101007216.html?s=01&t=0', '民营公司  ', '3-4年经验', '北京-海淀区', '本科', '岗位职责：1、在敏捷开发模式下，与其他团队成员一起，完成新需求的开发，缺陷的修复及系统测试；2、通过日常研究和实践，敢于提出改进主张，不断提升应用系统各方面性能；3、按时按质完成分配的工作及相关文档的撰写。任职要求：1、3年工作经验，熟悉Java8新语言特性，或者熟悉Scala、Kotlin等JVM语言 ；2、熟悉Spring/Spring Boot、SpringMVC、 MyBatis或者JooQ等开源框架，有具体的项目应用经验；3、熟悉Spring Cloud系列框架，具备微服务架构的开发经验；4、熟悉包括Apache ActiveMQ、RabbitMQ、Kafka等在内的至少一种消息中间件；5、有使用MySQL, Oracle等关系型数据库的经验并熟悉其数据类型和常用语法；6、有过OpenApi(Swagger)、自动化验证测试经验的候选人优先。', '2018-04-13', '2018-04-13 17:32:43', null);
