INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (32773, 'web前端开发工程师', '10000-20000元/月 ', '北京智慧流教育科技有限公司', 'http://jobs.zhaopin.com/295286132250237.htm', '民营', '3-5年', '北京', '本科', '岗位职责：1、负责产品的网站和手机终端的界面交互开发，为产品用户呈现最好的界面交互体验；2、优化网站性能，为用户体验提供坚实保障；3、整合通用UI解决方案，抽象化、模块化UI组件，让业务实现更简单；4、推进自动化，包括自动化测试、监控&amp;报警、开发工具等，不断的提高开发效率和代码质量；5、建设前端架构，针对业务定制最为高效、稳定且具备一定先进性的前端架构。任职要求：1、本科及以上学历，计算机、数学等相关专业毕业（或者计算机基础非常扎实）；2、两年以上的前端领域开发经验，能独立完成前端开发工作；3、精通HTML/CSS/Javascript等前端技术，能轻松写出符合W3C标准、兼容主流浏览器的代码；4、精通至少一种JS框架(AngularJS/NodeJS)或者对JS语言掌握较深；5、掌握至少一门非前端开发语言（如PHP/Java/Python/Ruby/C#等），并有一定的实战经验；6、热衷学习与自我修炼，喜欢阅读英文原版技术文档和书籍。7、有以下特点优先考虑：  √ 技术社区活跃分子  √ 有开源作品或技术博客', '2018-04-13 14:26:39', '2018-04-13 17:33:47', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (32774, '测试工程师', '8000-16000元/月 ', '北京智慧流教育科技有限公司', 'http://jobs.zhaopin.com/295286132250185.htm', '民营', '3-5年', '北京', '本科', '任职要求：1、计算机软件相关专业，统招本科及以上学历；2、至少具有3年以上测试经验，能够熟练编写测试用例。拥有完整的大项目测试经验者优先；3、熟练使用任意一款测试工具如loadrunner或jmeter，4、拥有APP和web测试经验，掌握黑盒测试范围以及测试策略5、熟悉使用自动化工具进行测试，熟悉自动化架构设计、实施；6、熟悉java/c/.net等开发语言一种，有数据库操作经验，有相关开发经验者优先；7、具备较高的分析问题和处理问题的能力；认真细心，逻辑思维清晰严谨；8、具备良好的沟通协作能力，责任感强，适应能力强', '2018-04-13 14:26:31', '2018-04-13 17:33:50', null);
