INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (32782, '数据分析师', '15001-20000元/月 ', '豆丁世纪(北京)网络技术有限公司', 'http://jobs.zhaopin.com/CC174557522J00033146115.htm', '合资', '3-5年', '北京', '本科', '工作职责：1、定期整理汇报相关统计数据报表；2、负责构建产品、运营及活动用户行为评估体系，通过数据分析对产品、运营、市场提出建议并推动实施；3、负责用户行为数据分析，通过监控及分析，推动产品改进，运营调整；4、通过数据分析支持产品改进及新模式的探索；5、配合其他部门有关工作；职位要求：1、2年以上数据分析相关工作经验；2、良好的分析、归纳和总结能力； 主动性强，有较强的责任心，良好的沟通能力，有团队协作精神；3、能熟练使用Excel、SQL、shell、Awk/Sed；熟悉unix/linux系统；4、会用Python/java编程解决实际问题；能利用Hadoop平台进行大规模计算者优先考虑；5、熟练掌握多种统计和挖掘方法，并有相关应用经验者优先考虑；6、具有金融行业项目经验的相关经验者优先考虑。', '2018-04-13 14:07:12', '2018-04-13 17:34:05', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (32783, '大数据工程师', '15000-30000元/月 ', '北京明宇未来科技有限公司', 'http://jobs.zhaopin.com/CZ585145080J00036300812.htm', '保密', '3-5年', '北京', '本科', '岗位职责:1.负责大数据项目的数据集成数据清洗和数据加工实施工作；3.负责Hadoop平台（分布式存储、分布式资源管理和调度、分布式计算等）的架构规划、设计、搭建工作；2.独立或者带领团队完成各种面向业务目标的数据建模和应用开发；4.指导数据工程师、分析流程和应用开发岗位要求：1.本科及以上学历，计算机、自动化、通信等相关专业，具有3年以上软件研发管理以及大数据开发的经验；2.熟练掌握Java语言，并熟悉Python语言，熟悉分布式系统架构，有hadoop，spark系统架构经验3.熟悉实时计算框架storm，flink，sparkstreaming其中之一；4.具有扎实的编程基础熟练使用SQL，熟悉数据库原理，熟练使用至少一种主流关系型数据库；5.熟悉使用任务调度框架oozie/azkaban等；6.有海量数据处理经验优先7.熟悉tensorflow优先7.工作积极主动，具备很强的责任心、团队合作精神、良好的语言表达及沟通交流能力', '2018-04-13 16:19:13', '2018-04-13 17:34:09', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (32784, '技术经理', '12000-20000元/月 ', '北京明宇未来科技有限公司', 'http://jobs.zhaopin.com/CZ585145080J00036292212.htm', '保密', '3-5年', '北京', '本科', '岗位职责：1、根据需求制定设计方案。2、开发程序主要逻辑，设计接口。3、数据库的优化与调整。4、保证程序逻辑缜密，在保证无BUG前提下尽量保证系统的兼容性和健壮性 。5、配合运维人员，保证服务器的高效与稳定。6、对程序进行优化和快速迭代。任职要求：1、大专及以上学历，计算机相关专业，4年以上Java及App开发经验。2、精通Java语言，熟悉多线程、IO，熟悉基本设计模式。3、有丰富的行业开发经验 ，金融行业优先。 4、精通HTTP协议和servlet规范，精通SSH设计和开发。5、精通常用应用服务器产品，如Tomcat，熟悉Linux系统。6、精通j2EE技术，精通struts、struts2、spring、hibernate 、svn、maven、mybatis 7、精通Redis、消息队列等主流中间件技术；8、掌握SQL语言，熟悉以下数据库：SQL server、Oracle、MySQL。9、有设计，调试接口的相关经验；10、对缓存、分布式事务、高并发、分布式等方面的技术开发经验者优先考虑。 	11、参与或者设计过大型系统，工作经验最少4年以上。12、工作认真，细心，有条理，具有较强的沟通能力及团队合作精神。 13、对互联网及金融领域开发有很强的兴趣，喜欢尝试最新的技术。', '2018-04-13 16:19:13', '2018-04-13 17:34:10', null);
